+use std::collections::HashSet;
+use std::sync::Arc;
+
+use actix_web::{HttpMessage, HttpRequest, HttpResponse, get, web};
+use chrono::NaiveDate;
+use serde::{Deserialize, Serialize};
+use sqlx::PgPool;
+
+use crate::error::AppError;
+use crate::middleware::api_key_auth::AuthContext;
+use crate::services::business_day;
+use crate::services::custom_holiday_cache::CustomHolidayCache;
+use crate::services::holiday_cache::HolidayCache;
+use crate::services::usage_tracker::UsageBatcher;
+use crate::utils::response::ApiResponse;
+
+#[derive(Deserialize)]
+pub struct BusinessDayCountQuery {
+    pub from: String,
+    pub to: String,
+}
+
+#[derive(Deserialize)]
+pub struct BusinessDayCalcQuery {
+    pub from: String,
+    pub days: Option<i32>,
+}
+
+#[derive(Serialize)]
+pub struct BusinessDayCountResponse {
+    pub from: String,
+    pub to: String,
+    pub business_days: i32,
+    pub total_days: i32,
+    pub holidays_in_range: i32,
+}
+
+#[derive(Serialize)]
+pub struct BusinessDayCalcResponse {
+    pub from: String,
+    pub days: i32,
+    pub result_date: String,
+}
+
+fn parse_date(s: &str) -> Result<NaiveDate, AppError> {
+    NaiveDate::parse_from_str(s, "%Y-%m-%d").map_err(|_| {
+        AppError::BadRequest(format!("Invalid date format: '{s}'. Expected YYYY-MM-DD"))
+    })
+}
+
+fn extract_auth(req: &HttpRequest) -> Result<AuthContext, AppError> {
+    let extensions = req.extensions();
+    extensions
+        .get::<AuthContext>()
+        .ok_or_else(|| AppError::Unauthorized("Missing auth context".to_string()))
+        .cloned()
+}
+
+fn validate_days(query: &BusinessDayCalcQuery) -> Result<i32, AppError> {
+    let days = query
+        .days
+        .ok_or_else(|| AppError::BadRequest("'days' parameter is required".to_string()))?;
+    if !(1..=3650).contains(&days) {
+        return Err(AppError::BadRequest(
+            "'days' must be between 1 and 3650".to_string(),
+        ));
+    }
+    Ok(days)
+}
+
+async fn build_holiday_set(
+    pool: &PgPool,
+    holiday_cache: &HolidayCache,
+    custom_holiday_cache: &CustomHolidayCache,
+    user_id: uuid::Uuid,
+) -> Result<HashSet<NaiveDate>, AppError> {
+    let far_past = NaiveDate::from_ymd_opt(2000, 1, 1).unwrap();
+    let far_future = NaiveDate::from_ymd_opt(2100, 12, 31).unwrap();
+
+    let public_holidays = holiday_cache
+        .get_holidays(pool, far_past, Some(far_future))
+        .await?;
+    let custom_holidays = custom_holiday_cache
+        .get_holidays(pool, user_id, far_past, Some(far_future))
+        .await?;
+
+    let mut set = HashSet::with_capacity(public_holidays.len() + custom_holidays.len());
+    for h in &public_holidays {
+        set.insert(h.date);
+    }
+    for h in &custom_holidays {
+        set.insert(h.date);
+    }
+    Ok(set)
+}
+
+#[get("/v1/business-days/count")]
+pub async fn get_business_days_count(
+    req: HttpRequest,
+    pool: web::Data<PgPool>,
+    holiday_cache: web::Data<HolidayCache>,
+    custom_holiday_cache: web::Data<CustomHolidayCache>,
+    usage_batcher: web::Data<Arc<UsageBatcher>>,
+    query: web::Query<BusinessDayCountQuery>,
+) -> Result<HttpResponse, AppError> {
+    let auth = extract_auth(&req)?;
+
+    let from = parse_date(&query.from)?;
+    let to = parse_date(&query.to)?;
+
+    if from > to {
+        return Err(AppError::BadRequest(
+            "'from' must be less than or equal to 'to'".to_string(),
+        ));
+    }
+
+    let holidays = build_holiday_set(
+        pool.get_ref(),
+        &holiday_cache,
+        &custom_holiday_cache,
+        auth.user_id,
+    )
+    .await?;
+
+    let biz_days = business_day::count_business_days(from, to, &holidays);
+    let total_days = (to - from).num_days() as i32 + 1;
+    let holidays_in_range = holidays.iter().filter(|d| **d >= from && **d <= to).count() as i32;
+
+    usage_batcher.increment(auth.api_key_id).await;
+
+    Ok(
+        HttpResponse::Ok().json(ApiResponse::ok(BusinessDayCountResponse {
+            from: query.from.clone(),
+            to: query.to.clone(),
+            business_days: biz_days,
+            total_days,
+            holidays_in_range,
+        })),
+    )
+}
+
+#[get("/v1/business-days/add")]
+pub async fn get_business_days_add(
+    req: HttpRequest,
+    pool: web::Data<PgPool>,
+    holiday_cache: web::Data<HolidayCache>,
+    custom_holiday_cache: web::Data<CustomHolidayCache>,
+    usage_batcher: web::Data<Arc<UsageBatcher>>,
+    query: web::Query<BusinessDayCalcQuery>,
+) -> Result<HttpResponse, AppError> {
+    let auth = extract_auth(&req)?;
+    let from = parse_date(&query.from)?;
+    let days = validate_days(&query)?;
+
+    let holidays = build_holiday_set(
+        pool.get_ref(),
+        &holiday_cache,
+        &custom_holiday_cache,
+        auth.user_id,
+    )
+    .await?;
+
+    let result_date = business_day::add_business_days(from, days, &holidays);
+
+    usage_batcher.increment(auth.api_key_id).await;
+
+    Ok(
+        HttpResponse::Ok().json(ApiResponse::ok(BusinessDayCalcResponse {
+            from: query.from.clone(),
+            days,
+            result_date: result_date.to_string(),
+        })),
+    )
+}
+
+#[get("/v1/business-days/subtract")]
+pub async fn get_business_days_subtract(
+    req: HttpRequest,
+    pool: web::Data<PgPool>,
+    holiday_cache: web::Data<HolidayCache>,
+    custom_holiday_cache: web::Data<CustomHolidayCache>,
+    usage_batcher: web::Data<Arc<UsageBatcher>>,
+    query: web::Query<BusinessDayCalcQuery>,
+) -> Result<HttpResponse, AppError> {
+    let auth = extract_auth(&req)?;
+    let from = parse_date(&query.from)?;
+    let days = validate_days(&query)?;
+
+    let holidays = build_holiday_set(
+        pool.get_ref(),
+        &holiday_cache,
+        &custom_holiday_cache,
+        auth.user_id,
+    )
+    .await?;
+
+    let result_date = business_day::subtract_business_days(from, days, &holidays);
+
+    usage_batcher.increment(auth.api_key_id).await;
+
+    Ok(
+        HttpResponse::Ok().json(ApiResponse::ok(BusinessDayCalcResponse {
+            from: query.from.clone(),
+            days,
+            result_date: result_date.to_string(),
+        })),
+    )
+}
