}

export default function LoginPage() {
  const router = useRouter()
  const [email, setEmail] = useState("")
  const [isLoadingOAuth, setIsLoadingOAuth] = useState<"google" | "github" | null>(null)
  const [isLoadingMagicLink, setIsLoadingMagicLink] = useState(false)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    const supabase = createClient()
    supabase.auth.getUser().then(({ data: { user } }) => {
      if (user) router.push("/dashboard")
    })

    // Check for error in URL params
    const urlParams = new URLSearchParams(window.location.search)
    const errorParam = urlParams.get("error")
    if (errorParam === "auth") {
      setError("인증에 실패했습니다. 다시 시도해주세요.")
    }
  }, [router])

  const handleOAuthLogin = async (provider: "google" | "github") => {
    setIsLoadingOAuth(provider)
    setError(null)
    try {
      const supabase = createClient()
      const { error } = await supabase.auth.signInWithOAuth({
        provider,
        options: { redirectTo: `${window.location.origin}/auth/callback` },
      })
      if (error) throw error
    } catch (err) {
      setError(err instanceof Error ? err.message : "인증에 실패했습니다.")
      setIsLoadingOAuth(null)
    }
  }

  const handleMagicLink = async () => {
    if (!email) {
      setError("이메일을 입력해주세요.")
      return
    }
    setIsLoadingMagicLink(true)
    setError(null)
    try {
      const supabase = createClient()
      const { error } = await supabase.auth.signInWithOtp({
        email,
        options: { emailRedirectTo: `${window.location.origin}/auth/callback` },
      })
      if (error) throw error
      toast.success("매직 링크가 이메일로 전송되었습니다.")
    } catch (err) {
      setError(err instanceof Error ? err.message : "매직 링크 전송에 실패했습니다.")
    } finally {
      setIsLoadingMagicLink(false)
    }
  }

  return (
    <div className="flex min-h-screen flex-col items-center justify-center px-6">
      <div className="pointer-events-none absolute inset-0">
        <div className="absolute left-1/2 top-1/3 h-[400px] w-[400px] -translate-x-1/2 -translate-y-1/2 rounded-full bg-primary/5 blur-3xl" />
      </div>

      <div className="relative z-10 w-full max-w-sm">
        <div className="mb-8 flex flex-col items-center text-center">
          <HuDyLogo size="lg" href="/" showText={false} />
          <h1 className="mt-3 text-2xl font-bold text-foreground">HuDy</h1>
          <p className="mt-2 text-sm text-muted-foreground">{"로그인하고 API를 시작하세요"}</p>
        </div>

        <div className="rounded-xl border border-border bg-card p-6">
          {error && (
            <div className="mb-4 rounded-lg border border-red-500/20 bg-red-500/10 px-4 py-3 text-sm text-red-500">
              {error}
            </div>
          )}

          <div className="flex flex-col gap-3">
            <Button
              variant="outline"
              size="lg"
              className="w-full gap-3 bg-transparent"
              onClick={() => handleOAuthLogin("google")}
              disabled={isLoadingOAuth !== null || isLoadingMagicLink}
            >
              {isLoadingOAuth === "google" ? (
                <Loader2 className="h-5 w-5 animate-spin" />
              ) : (
                <GoogleIcon className="h-5 w-5" />
              )}
              <span>Google로 계속하기</span>
            </Button>

            <Button
              variant="outline"
              size="lg"
              className="w-full gap-3 bg-transparent"
              onClick={() => handleOAuthLogin("github")}
              disabled={isLoadingOAuth !== null || isLoadingMagicLink}
            >
              {isLoadingOAuth === "github" ? (
                <Loader2 className="h-5 w-5 animate-spin" />
              ) : (
                <GithubIcon className="h-5 w-5" />
              )}
              <span>GitHub로 계속하기</span>
            </Button>
          </div>

          <div className="my-6 flex items-center gap-3">
            <div className="h-px flex-1 bg-border" />
            <span className="text-xs text-muted-foreground">OR</span>
            <div className="h-px flex-1 bg-border" />
          </div>

          <div className="flex flex-col gap-3">
            <div>
              <label htmlFor="email" className="mb-1.5 block text-sm text-muted-foreground">
                {"이메일"}
              </label>
              <input
                id="email"
                type="email"
                placeholder="you@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleMagicLink()}
                disabled={isLoadingOAuth !== null || isLoadingMagicLink}
                className="w-full rounded-lg border border-border bg-background px-4 py-2.5 text-sm text-foreground placeholder:text-muted-foreground focus:border-primary focus:outline-none focus:ring-1 focus:ring-primary disabled:opacity-50"
              />
            </div>
            <Button
              size="lg"
              className="w-full"
              onClick={handleMagicLink}
              disabled={isLoadingOAuth !== null || isLoadingMagicLink}
            >
              {isLoadingMagicLink ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  {"전송 중..."}
                </>
              ) : (
                "매직 링크로 로그인"
              )}
            </Button>
          </div>
        </div>

        <p className="mt-6 text-center text-xs text-muted-foreground">
          {"로그인하면 "}
          <Link href="/terms" className="text-foreground underline underline-offset-2 hover:text-primary">이용약관</Link>
          {" 및 "}
          <Link href="/privacy" className="text-foreground underline underline-offset-2 hover:text-primary">개인정보처리방침</Link>
          {"에 동의하는 것으로 간주됩니다."}
        </p>
      </div>
    </div>
  )
}

import { Calendar, CalendarCheck, Code2, Layers, Package, Zap } from "lucide-react"

const features = [
  {
    icon: Calendar,
    title: "공휴일 조회",
    description: "대한민국 법정 공휴일, 대체 공휴일, 임시 공휴일까지 모두 정확하게 조회할 수 있습니다.",
  },
  {
    icon: Layers,
    title: "커스텀 공휴일",
    description: "회사 창립기념일, 사내 행사일 등 나만의 커스텀 공휴일을 등록하고 API로 조회하세요.",
  },
  {
    icon: CalendarCheck,
    title: "영업일 관리",
    description: "특정 날짜의 영업일 여부 확인, 기간 내 영업일 수 계산, N영업일 후/전 날짜 계산을 지원합니다.",
  },
  {
    icon: Package,
    title: "공식 SDK",
    description: "Node.js와 Python SDK를 제공합니다. npm install 또는 pip install 한 줄이면 바로 시작.",
  },
  {
    icon: Zap,
    title: "빠른 응답 속도",
    description: "어디서나 100ms 이내 응답. 안정적인 인프라로 서비스합니다.",
  },
  {
    icon: Code2,
    title: "개발자 친화적",
    description: "RESTful API, JSON 응답, MCP 지원. 어떤 언어에서든, AI Agent에서도 쉽게 연동할 수 있습니다.",
  },
]

export function FeaturesSection() {
  return (
    <section id="features" className="px-6 py-24">
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <p className="mb-3 text-sm font-medium uppercase tracking-widest text-primary">Features</p>
          <h2 className="text-balance text-3xl font-bold tracking-tight text-foreground md:text-4xl">
            {"개발자를 위해 설계된 API"}
          </h2>
          <p className="mx-auto mt-4 max-w-xl text-pretty text-muted-foreground">
            {"단순한 공휴일 조회를 넘어, 비즈니스에 필요한 모든 휴일 관련 기능을 제공합니다."}
          </p>
        </div>

        <div className="grid gap-6 md:grid-cols-2">
          {features.map((feature) => (
            <div
              key={feature.title}
              className="group rounded-xl border border-border bg-card p-6 transition-colors hover:border-primary/30 hover:bg-card/80"
            >
              <div className="mb-4 flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
                <feature.icon className="h-5 w-5 text-primary" />
              </div>
              <h3 className="mb-2 text-lg font-semibold text-foreground">{feature.title}</h3>
              <p className="text-sm leading-relaxed text-muted-foreground">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}

