import Image from "next/image"
import Link from "next/link"

interface HuDyLogoProps {
  size?: "sm" | "md" | "lg"
  href?: string
  showText?: boolean
}

const sizes = {
  sm: { icon: 28, text: "text-lg" },
  md: { icon: 32, text: "text-xl" },
  lg: { icon: 40, text: "text-2xl" },
}

export function HuDyLogo({ size = "md", href = "/", showText = true }: HuDyLogoProps) {
  const s = sizes[size]

  const content = (
    <span className="flex items-center gap-2">
      <Image
        src="/hudy-icon.webp"
        alt="HuDy logo"
        width={s.icon}
        height={s.icon}
        className="rounded-lg"
      />
      {showText && (
        <span className={`font-bold tracking-tight text-foreground ${s.text}`}>HuDy</span>
      )}
    </span>
  )

  if (href) {
    return <Link href={href} className="flex items-center gap-2">{content}</Link>
  }

  return content
}

import React from "react"
import type { Metadata, Viewport } from 'next'
import { Inter, JetBrains_Mono } from 'next/font/google'

import './globals.css'
import { Toaster } from '@/components/ui/sonner'

const _inter = Inter({ subsets: ['latin'], variable: '--font-inter' })
const _jetbrainsMono = JetBrains_Mono({ subsets: ['latin'], variable: '--font-jetbrains' })

export const metadata: Metadata = {
  title: 'HuDy - 대한민국 공휴일 API',
  description: '대한민국 공휴일을 쉽게 조회하고, 나만의 커스텀 공휴일을 추가하세요. 개발자를 위한 프리미엄 공휴일 API 서비스.',
}

export const viewport: Viewport = {
  themeColor: '#0a0a0a',
}

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode
}>) {
  return (
    <html lang="ko">
      <body className="font-sans antialiased">
        {children}
        <Toaster />
      </body>
    </html>
  )
}

