+use chrono::{Datelike, NaiveDate, Weekday};
+use std::collections::HashSet;
+
+pub fn is_business_day(date: NaiveDate, holidays: &HashSet<NaiveDate>) -> bool {
+    let weekday = date.weekday();
+    weekday != Weekday::Sat && weekday != Weekday::Sun && !holidays.contains(&date)
+}
+
+pub fn count_business_days(from: NaiveDate, to: NaiveDate, holidays: &HashSet<NaiveDate>) -> i32 {
+    let mut count = 0;
+    let mut current = from;
+    while current <= to {
+        if is_business_day(current, holidays) {
+            count += 1;
+        }
+        current = current.succ_opt().expect("date overflow");
+    }
+    count
+}
+
+pub fn add_business_days(from: NaiveDate, days: i32, holidays: &HashSet<NaiveDate>) -> NaiveDate {
+    let mut count = 0;
+    let mut current = from;
+    while count < days {
+        current = current.succ_opt().expect("date overflow");
+        if is_business_day(current, holidays) {
+            count += 1;
+        }
+    }
+    current
+}
+
+pub fn subtract_business_days(
+    from: NaiveDate,
+    days: i32,
+    holidays: &HashSet<NaiveDate>,
+) -> NaiveDate {
+    let mut count = 0;
+    let mut current = from;
+    while count < days {
+        current = current.pred_opt().expect("date underflow");
+        if is_business_day(current, holidays) {
+            count += 1;
+        }
+    }
+    current
+}
