  1→import { PADDLE_PRICE_TIERS } from "./pricing-config";
  2→import { createServerSupabase } from "@/lib/supabase/server";
  3→
  4→interface TierInfo {
  5→  tier: (typeof PADDLE_PRICE_TIERS)[number];
  6→  cycle: "month" | "year";
  7→  hasTrial: boolean;
  8→}
  9→
 10→/**
 11→ * 서버 측에서 priceId를 검증하고, 필요시 무료체험 없는 버전으로 교체합니다.
 12→ *
 13→ * @param requestedPriceId - 클라이언트가 요청한 Paddle Price ID
 14→ * @param userEmail - 현재 사용자 이메일 (로그인 상태인 경우)
 15→ * @returns 검증된 Price ID (무료체험 이력이 있으면 noTrial 버전으로 교체)
 16→ */
 17→export async function validateAndGetPriceId(
 18→  requestedPriceId: string,
 19→  userEmail?: string
 20→): Promise<string> {
 21→  // 1. 요청된 priceId가 어느 플랜/주기인지 확인
 22→  const tierInfo = findTierByPriceId(requestedPriceId);
 23→
 24→  if (!tierInfo) {
 25→    // 유효하지 않은 priceId면 그대로 반환 (Paddle이 거부할 것)
 26→    console.warn(`Unknown priceId requested: ${requestedPriceId}`);
 27→    return requestedPriceId;
 28→  }
 29→
 30→  const { tier, cycle, hasTrial } = tierInfo;
 31→
 32→  // 2. 무료체험이 없는 priceId면 그대로 반환
 33→  if (!hasTrial) {
 34→    return requestedPriceId;
 35→  }
 36→
 37→  // 3. 로그인하지 않은 사용자는 체험 허용
 38→  if (!userEmail) {
 39→    return requestedPriceId;
 40→  }
 41→
 42→  const supabase = await createServerSupabase();
 43→
 44→  // 4. customers 테이블에서 무료체험 이력 확인
 45→  const { data: customer } = await supabase
 46→    .from("customers")
 47→    .select("has_used_free_trial")
 48→    .eq("email", userEmail)
 49→    .single();
 50→
 51→  // 5. customer 레코드가 없으면 첫 구매 → 무료체험 허용
 52→  if (!customer) {
 53→    console.log(`No customer record for ${userEmail}, allowing free trial`);
 54→    return requestedPriceId;
 55→  }
 56→
 57→  // 6. 무료체험 사용 이력이 있으면 noTrial 버전으로 교체
 58→  if (customer.has_used_free_trial) {
 59→    const noTrialPriceId =
 60→      cycle === "month" ? tier.priceId.monthNoTrial : tier.priceId.yearNoTrial;
 61→
 62→    if (noTrialPriceId) {
 63→      console.log(
 64→        `Price validation: ${userEmail} requested ${requestedPriceId}, returned ${noTrialPriceId} (trial already used)`
 65→      );
 66→      return noTrialPriceId;
 67→    } else {
 68→      console.warn(
 69→        `No noTrial priceId available for ${tier.name} ${cycle}, falling back to original`
 70→      );
 71→      return requestedPriceId;
 72→    }
 73→  }
 74→
 75→  // 7. 이력 없으면 원래 priceId 반환 (무료체험 제공)
 76→  console.log(`Price validation: ${userEmail} eligible for free trial`);
 77→  return requestedPriceId;
 78→}
 79→
 80→/**
 81→ * Paddle Price ID로부터 해당하는 플랜, 주기, 무료체험 여부를 찾습니다.
 82→ */
 83→function findTierByPriceId(priceId: string): TierInfo | null {
 84→  for (const tier of PADDLE_PRICE_TIERS) {
 85→    // 월간 플랜 (무료체험 포함)
 86→    if (tier.priceId.month === priceId) {
 87→      return {
 88→        tier,
 89→        cycle: "month",
 90→        hasTrial: !!tier.priceId.monthNoTrial,
 91→      };
 92→    }
 93→
 94→    // 연간 플랜 (무료체험 포함)
 95→    if (tier.priceId.year === priceId) {
 96→      return {
 97→        tier,
 98→        cycle: "year",
 99→        hasTrial: !!tier.priceId.yearNoTrial,
100→      };
101→    }
102→
103→    // 월간 플랜 (무료체험 없음)
104→    if (tier.priceId.monthNoTrial === priceId) {
105→      return {
106→        tier,
107→        cycle: "month",
108→        hasTrial: false,
109→      };
110→    }
111→
112→    // 연간 플랜 (무료체험 없음)
113→    if (tier.priceId.yearNoTrial === priceId) {
114→      return {
115→        tier,
116→        cycle: "year",
117→        hasTrial: false,
118→      };
119→    }
120→  }
121→
122→  return null;
123→}
