 5→export function getPaddleInstance(): Paddle {
 6→  if (!paddleInstance) {
 7→    const apiKey = process.env.PADDLE_API_KEY;
 8→
 9→    if (!apiKey) {
10→      throw new Error("PADDLE_API_KEY environment variable is not set");
