 1→import { Paddle, PricePreviewParams, PricePreviewResponse } from "@paddle/paddle-js";
 2→import { useEffect, useState } from "react";
 3→import { PADDLE_PRICE_TIERS } from "@/lib/paddle/pricing-config";
 4→
 5→export type PaddlePrices = Record<string, string>;
 6→
 7→// Paddle Price ID 패턴: pri_[a-z\d]{26}
 8→const PADDLE_PRICE_ID_PATTERN = /^pri_[a-z\d]{26}$/;
 9→
10→function isValidPaddlePriceId(priceId: string): boolean {
11→  return PADDLE_PRICE_ID_PATTERN.test(priceId);
12→}
13→
14→function getLineItems(): PricePreviewParams["items"] {
15→  const priceIds = PADDLE_PRICE_TIERS
16→    .flatMap((tier) => [tier.priceId.month, tier.priceId.year])
17→    .filter(isValidPaddlePriceId); // 유효한 Paddle Price ID만 필터링
18→
19→  return priceIds.map((priceId) => ({ priceId, quantity: 1 }));
20→}
21→
22→function getPriceAmounts(prices: PricePreviewResponse) {
23→  return prices.data.details.lineItems.reduce((acc, item) => {
24→    acc[item.price.id] = item.formattedTotals.total;
25→    return acc;
26→  }, {} as PaddlePrices);
27→}
28→
29→export function usePaddlePrices(
30→  paddle: Paddle | undefined,
31→  country: string = "US"
32→): { prices: PaddlePrices; loading: boolean; error: string | null } {
33→  const [prices, setPrices] = useState<PaddlePrices>({});
34→  const [loading, setLoading] = useState<boolean>(true);
35→  const [error, setError] = useState<string | null>(null);
36→
37→  useEffect(() => {
38→    if (!paddle) {
39→      setLoading(false);
40→      return;
41→    }
42→
43→    const paddlePricePreviewRequest: Partial<PricePreviewParams> = {
44→      items: getLineItems(),
45→      ...(country !== "OTHERS" && { address: { countryCode: country } }),
46→    };
47→
48→    setLoading(true);
49→    setError(null);
50→
51→    paddle
52→      ?.PricePreview(paddlePricePreviewRequest as PricePreviewParams)
53→      .then((pricesData) => {
54→        setPrices((prevState) => ({ ...prevState, ...getPriceAmounts(pricesData) }));
55→        setLoading(false);
56→      })
57→      .catch((err) => {
58→        console.error("Failed to fetch Paddle prices:", err);
59→        setError("Failed to load pricing information");
60→        setLoading(false);
61→      });
62→  }, [country, paddle]);
63→
64→  return { prices, loading, error };
65→}
