 30→export function PricingPage() {
 31→  const { t, language } = useLanguage();
 32→  const router = useRouter();
 33→  const [paddle, setPaddle] = useState<Paddle | undefined>(undefined);
 34→  const { prices: paddlePrices, loading: paddleLoading } =
 35→    usePaddlePrices(paddle);
 36→  const [billingCycle, setBillingCycle] = useState<"monthly" | "yearly">(
 37→    "monthly"
 38→  );
 39→  const [creditQuantity, setCreditQuantity] = useState(5);
 40→
 41→  // Static plan mapping based on plan names
 42→  const getPlanDescription = (planName: string) => {
 43→    const planKey = planName.toLowerCase();
 44→    return t(`pricingPage.plans.${planKey}.description`);
 45→  };
 46→
 47→  function extractFeaturesByLanguage(raw: unknown): string[] {
 48→    try {
 49→      if (!raw) return [];
 50→      if (Array.isArray(raw) && raw.every((x) => typeof x === "string")) {
 51→        return raw as string[];
 52→      }
 53→      if (typeof raw === "string") {
 54→        const parsed = JSON.parse(raw);
 55→        if (
 56→          parsed &&
 57→          typeof parsed === "object" &&
 58→          Array.isArray(parsed[language])
 59→        ) {
 60→          return parsed[language] as string[];
 61→        }
 62→      }
 63→      if (
 64→        typeof raw === "object" &&
 65→        raw !== null &&
 66→        Array.isArray((raw as any)[language])
 67→      ) {
 68→        return (raw as Record<string, string[]>)[language] || [];
 69→      }
 70→    } catch (e) {
 71→      console.warn("Failed to parse plan features by language", e);
 72→    }
 73→    return [];
 74→  }
 75→  const [plans, setPlans] = useState<SubscriptionPlan[]>([]);
 76→  const [currentSubscription, setCurrentSubscription] =
 77→    useState<Subscription | null>(null);
 78→  const [loading, setLoading] = useState(true);
 79→  const [error, setError] = useState<string | null>(null);
 80→
 81→  useEffect(() => {
 82→    async function fetchData() {
 83→      try {
 84→        const [plansResult, subscriptionResult] = await Promise.all([
 85→          getSubscriptionPlans(),
 86→          getCurrentSubscription(),
 87→        ]);
 88→
 89→        if (plansResult.error) {
 90→          setError(plansResult.error);
 91→          return;
 92→        }
 93→
 94→        // 플랜 순서 정렬: order 컬럼 사용
 95→        const sortedPlans = plansResult.plans.sort((a, b) => a.order - b.order);
