 49→/**
 50→ * Get current user's subscription with plan details
 51→ */
 52→export async function getCurrentSubscription(): Promise<{
 53→  subscription: Subscription | null;
 54→  error?: string;
 55→}> {
 56→  try {
 57→    const supabase = await createServerSupabase();
 58→
 59→    // Get current user
 60→    const {
 61→      data: { user },
 62→      error: authError,
 63→    } = await supabase.auth.getUser();
 64→
 65→    if (authError || !user) {
 66→      return {
 67→        subscription: null,
 68→        error: "User not authenticated",
 69→      };
 70→    }
 71→
 72→    // Get user's current subscription with plan details
 73→    // Note: We don't filter by plan.is_active because users may have
 74→    // subscriptions to hidden/inactive plans (e.g., Enterprise)
 75→    // IMPORTANT: Also check ends_at to handle expired subscriptions
 76→    // even if webhook hasn't updated status yet
 77→    const now = new Date().toISOString();
 78→    const { data: subscription, error } = await supabase
 79→      .from("subscriptions")
 80→      .select(
 81→        `
 82→        *,
 83→        plan:subscription_plans!plan_id(*)
 84→      `
 85→      )
 86→      .eq("user_id", user.id)
 87→      .eq("status", "active")
 88→      .or(`ends_at.is.null,ends_at.gt.${now}`)
 89→      .single();
 90→
 91→    if (error) {
 92→      // No rows -> user has no active subscription; not an error
 93→      if ((error as any).code === "PGRST116") {
 94→        return { subscription: null };
 95→      }
 96→      console.error("[getCurrentSubscription] Error:", error, "for user:", user.id);
 97→      return {
 98→        subscription: null,
 99→        error: "Failed to get subscription",
100→      };
101→    }
102→
103→    return {
104→      subscription: subscription as Subscription,
105→    };
106→  } catch (error) {
107→    console.error("Get subscription error:", error);
108→    return {
109→      subscription: null,
110→      error: "An unexpected error occurred",
111→    };
112→  }
113→}
114→
115→/**
116→ * Get current month's usage for the user
117→ */
118→export async function getCurrentMonthUsage(): Promise<{
119→  usage: MonthlyUsage | null;
120→  error?: string;
121→}> {
122→  try {
123→    const supabase = await createServerSupabase();
124→
125→    // Get current user
126→    const {
127→      data: { user },
128→      error: authError,
129→    } = await supabase.auth.getUser();
130→
131→    if (authError || !user) {
132→      return {
133→        usage: null,
134→        error: "User not authenticated",
135→      };
136→    }
137→
138→    const currentMonth = new Date().toISOString().slice(0, 7); // YYYY-MM format
139→
140→    // Get or create monthly usage record
141→    let { data: usage, error } = await supabase
142→      .from("monthly_usage")
143→      .select("*")
144→      .eq("user_id", user.id)
145→      .eq("year_month", currentMonth)
146→      .single();
147→
148→    if (error && error.code === "PGRST116") {
149→      // Record doesn't exist, create it
150→      const { data: newUsage, error: createError } = await supabase
151→        .from("monthly_usage")
152→        .insert({
153→          user_id: user.id,
154→          year_month: currentMonth,
155→          documents_created: 0,
156→          published_completed_count: 0,
157→        })
158→        .select()
159→        .single();
160→
161→      if (createError) {
162→        console.error("Create usage record error:", createError);
163→        return {
164→          usage: null,
165→          error: "Failed to create usage record",
166→        };
167→      }
168→
169→      usage = newUsage;
170→    } else if (error) {
171→      console.error("Get usage error:", error);
172→      return {
173→        usage: null,
174→        error: "Failed to get usage",
175→      };
176→    }
177→
178→    return {
179→      usage: usage as MonthlyUsage,
180→    };
