339→/**
340→ * Check if user can create a new publication
341→ * A publication contains documents that will be published, so this checks
342→ * if the user has room for more active documents
343→ */
344→export async function canCreatePublication(documentCount: number = 1): Promise<{
345→  canCreate: boolean;
346→  reason?: string;
347→  error?: string;
348→}> {
349→  try {
350→    const { limits, error } = await getUserUsageLimits();
351→
352→    if (error || !limits) {
353→      return {
354→        canCreate: false,
355→        error: error || "Failed to check limits",
356→      };
357→    }
358→
359→    // Calculate total available (monthly + credits)
360→    const { credits, error: creditsError } = await getCreditBalance();
361→    if (creditsError) {
362→      return { canCreate: false, error: creditsError };
363→    }
364→
365→    const monthlyRemaining =
366→      limits.activeDocumentLimit === -1
367→        ? Infinity
368→        : limits.activeDocumentLimit - limits.currentActiveDocuments;
369→
370→    const totalAvailable =
371→      monthlyRemaining === Infinity
372→        ? Infinity
373→        : monthlyRemaining + (credits?.publish_credits || 0);
374→
375→    if (totalAvailable >= documentCount) {
376→      return { canCreate: true };
377→    }
378→
379→    return {
380→      canCreate: false,
381→      reason: `${documentCount}개 발행 불가 (사용 가능: ${totalAvailable}개)`,
382→    };
383→  } catch (error) {
384→    console.error("Can create publication error:", error);
385→    return {
386→      canCreate: false,
387→      error: "An unexpected error occurred",
388→    };
389→  }
390→}
