 1→import { NextRequest } from "next/server";
 2→import { ProcessWebhook } from "@/lib/paddle/process-webhook";
 3→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 4→
 5→const webhookProcessor = new ProcessWebhook();
 6→
 7→export async function POST(request: NextRequest) {
 8→  const signature = request.headers.get("paddle-signature") || "";
 9→  const rawRequestBody = await request.text();
10→  const privateKey = process.env.PADDLE_NOTIFICATION_WEBHOOK_SECRET || "";
11→
12→  try {
13→    if (!signature || !rawRequestBody) {
14→      console.error("Missing signature or request body");
15→      return Response.json({ error: "Missing signature from header" }, { status: 400 });
16→    }
17→
18→    if (!privateKey) {
19→      console.error("PADDLE_NOTIFICATION_WEBHOOK_SECRET not configured");
20→      return Response.json(
21→        { error: "Webhook secret not configured" },
22→        { status: 500 }
23→      );
24→    }
25→
26→    const paddle = getPaddleInstance();
27→    const eventData = await paddle.webhooks.unmarshal(
28→      rawRequestBody,
29→      privateKey,
30→      signature
31→    );
32→    const eventName = eventData?.eventType ?? "Unknown event";
33→
34→    console.log(`Received Paddle webhook: ${eventName}`);
35→
36→    if (eventData) {
37→      await webhookProcessor.processEvent(eventData);
38→      console.log(`Successfully processed webhook: ${eventName}`);
39→    }
40→
41→    return Response.json({ status: 200, eventName });
42→  } catch (e) {
43→    console.error("Paddle webhook error:", e);
44→    return Response.json(
45→      { error: "Internal server error", details: e instanceof Error ? e.message : "Unknown error" },
46→      { status: 500 }
47→    );
48→  }
49→}
