 411→  private async handleTransactionCompleted(
 412→    eventData: TransactionCompletedEvent
 413→  ) {
 414→    const supabase = createServiceSupabase();
 415→
 416→    try {
 417→      // Check if this is a credit purchase transaction
 418→      const customData = eventData.data.customData as CreditPurchaseCustomData | undefined;
 419→
 420→      if (customData?.type === "credit") {
 421→        console.log("[transaction.completed] Credit purchase detected");
 422→        await this.handleCreditPurchase(eventData, customData);
 423→        return;
 424→      }
 425→
 426→      const customerId = eventData.data.customerId;
 427→
 428→      if (!customerId) {
 429→        console.log("[transaction.completed] No customer_id in event");
 430→        return;
 431→      }
 432→
 433→      console.log(
 434→        `[transaction.completed] Processing for customer ${customerId}`
 435→      );
 436→
 437→      // 1. Paddle API로 customer 정보 가져오기 (실제 이메일 포함)
 438→      const paddle = getPaddleInstance();
 439→      let customerEmail: string | null = null;
 440→
 441→      try {
 442→        console.log(
 443→          `[transaction.completed] Fetching customer info from Paddle API...`
 444→        );
 445→        const paddleCustomer = await paddle.customers.get(customerId);
 446→        customerEmail = paddleCustomer.email;
 447→        console.log(
 448→          `[transaction.completed] Fetched customer email from Paddle: ${customerEmail}`
 449→        );
 450→      } catch (apiError) {
 451→        console.error(
 452→          `[transaction.completed] Failed to fetch customer from Paddle API:`,
 453→          apiError
 454→        );
 455→        // Paddle API 실패 시 fallback: DB에서 가져오기
 456→        const { data: dbCustomer } = await supabase
 457→          .from("customers")
 458→          .select("email")
 459→          .eq("customer_id", customerId)
 460→          .single();
 461→
 462→        if (dbCustomer?.email && !dbCustomer.email.includes("placeholder")) {
 463→          customerEmail = dbCustomer.email;
 464→          console.log(
 465→            `[transaction.completed] Using email from database: ${customerEmail}`
 466→          );
 467→        } else {
 468→          console.log(
 469→            `[transaction.completed] No valid email found, waiting for customer.created event`
 470→          );
 471→          return;
 472→        }
 473→      }
 474→
 475→      if (!customerEmail) {
 476→        console.log(
 477→          `[transaction.completed] No email available for customer ${customerId}`
 478→        );
 479→        return;
 480→      }
 481→
 482→      // 2. customers 테이블에서 customer 조회 또는 생성
 483→      let { data: customerData, error: customerError } = await supabase
 484→        .from("customers")
 485→        .select("user_id")
 486→        .eq("customer_id", customerId)
 487→        .single();
 488→
 489→      if (customerError || !customerData) {
 490→        console.log(
 491→          `[transaction.completed] Customer ${customerId} not in database, creating...`
 492→        );
 493→
 494→        // customer 레코드 생성 (실제 이메일 사용)
 495→        const { data: newCustomer, error: insertError } = await supabase
 496→          .from("customers")
 497→          .insert({
 498→            customer_id: customerId,
 499→            email: customerEmail,
 500→            user_id: null,
 501→          })
 502→          .select("user_id")
 503→          .single();
 504→
 505→        if (insertError) {
 506→          console.error(
 507→            `[transaction.completed] Failed to create customer:`,
 508→            insertError
 509→          );
 510→          return;
 511→        }
 512→
 513→        customerData = newCustomer;
 514→        console.log(
 515→          `[transaction.completed] Created customer ${customerId} with email ${customerEmail}`
 516→        );
 517→      }
 518→
 519→      // 3. user_id가 없으면 이메일로 user 찾아서 업데이트
 520→      if (!customerData.user_id) {
 521→        console.log(
 522→          `[transaction.completed] Looking up user by email: ${customerEmail}`
 523→        );
 524→
 525→        const {
 526→          data: { users },
 527→          error: listError,
 528→        } = await supabase.auth.admin.listUsers();
 529→
 530→        if (listError) {
 531→          console.error(
 532→            `[transaction.completed] Failed to list users:`,
 533→            listError
 534→          );
 535→          return;
 536→        }
 537→
 538→        const user = users?.find((u) => u.email === customerEmail);
 539→
 540→        if (user) {
 541→          console.log(
 542→            `[transaction.completed] Found user ${user.id} for email ${customerEmail}`
 543→          );
 544→
 545→          // 2a. customers 테이블에 user_id 업데이트
 546→          const { error: customerUpdateError } = await supabase
 547→            .from("customers")
 548→            .update({ user_id: user.id })
 549→            .eq("customer_id", customerId);
 550→
 551→          if (customerUpdateError) {
 552→            console.error(
 553→              `[transaction.completed] Failed to update customer with user_id:`,
 554→              customerUpdateError
 555→            );
 556→            return;
 557→          }
 558→
 559→          console.log(
 560→            `[transaction.completed] Updated customer ${customerId} with user_id ${user.id}`
 561→          );
 562→
 563→          // 2b. 이 customer의 모든 unlinked subscriptions 찾아서 연결
 564→          const { data: subscriptions, error: subsError } = await supabase
 565→            .from("subscriptions")
 566→            .select("id, paddle_subscription_id, status")
 567→            .eq("paddle_customer_id", customerId)
