 569→
 570→          if (subsError) {
 571→            console.error(
 572→              `[transaction.completed] Failed to fetch subscriptions:`,
 573→              subsError
 574→            );
 575→            return;
 576→          }
 577→
 578→          if (subscriptions && subscriptions.length > 0) {
 579→            console.log(
 580→              `[transaction.completed] Found ${subscriptions.length} unlinked subscription(s) for customer ${customerId}`
 581→            );
 582→
 583→            for (const sub of subscriptions) {
 584→              // subscription에 user_id 연결
 585→              const { error: subUpdateError } = await supabase
 586→                .from("subscriptions")
 587→                .update({
 588→                  user_id: user.id,
 589→                  updated_at: new Date().toISOString(),
 590→                })
 591→                .eq("id", sub.id);
 592→
 593→              if (subUpdateError) {
 594→                console.error(
 595→                  `[transaction.completed] Failed to link subscription ${sub.id}:`,
 596→                  subUpdateError
 597→                );
 598→                continue;
 599→              }
 600→
 601→              // 가장 최근 active subscription만 users 테이블에 업데이트
 602→              if (sub.status === "active" && sub === subscriptions[0]) {
 603→                const { error: userUpdateError } = await supabase
 604→                  .from("users")
 605→                  .update({ current_subscription_id: sub.id })
 606→                  .eq("id", user.id);
 607→
 608→                if (userUpdateError) {
 609→                  console.error(
 610→                    `[transaction.completed] Failed to update user's current_subscription_id:`,
 611→                    userUpdateError
 612→                  );
 613→                }
 614→              }
 615→
 616→              console.log(
 617→                `[transaction.completed] Successfully linked subscription ${sub.id} (${sub.paddle_subscription_id}) to user ${user.id}`
 618→              );
 619→            }
 620→
 621→            console.log(
 622→              `[transaction.completed] ✅ Successfully linked all subscriptions for customer ${customerId} to user ${user.id}`
 623→            );
 624→
 625→            // Check if this transaction includes a free trial and record usage
 626→            const priceId = eventData.data.items[0]?.price?.id;
 627→            if (priceId && this.isPriceWithTrial(priceId)) {
 628→              console.log(
 629→                `[transaction.completed] Free trial detected for priceId: ${priceId}`
 630→              );
 631→              await this.recordTrialUsage(customerId);
 632→            }
 633→
 634→            // Send payment notification
 635→            await this.sendPaymentNotification(subscriptions[0].id, supabase);
 636→          } else {
 637→            console.log(
 638→              `[transaction.completed] No unlinked subscriptions found for customer ${customerId}`
 639→            );
 640→          }
 641→        } else {
 642→          console.warn(
 643→            `[transaction.completed] No user found with email ${customerEmail} - user might not be registered yet`
 644→          );
 645→        }
 646→      } else if (customerData.user_id) {
 647→        console.log(
 648→          `[transaction.completed] Customer ${customerId} already linked to user ${customerData.user_id} - checking for unlinked subscriptions`
 649→        );
 650→
 651→        // customer는 이미 linked되어 있지만 subscription이 아직 linked 안 되어있을 수 있음
 652→        const { data: subscriptions } = await supabase
 653→          .from("subscriptions")
 654→          .select("id, paddle_subscription_id, status")
 655→          .eq("paddle_customer_id", customerId)
 656→          .is("user_id", null);
 657→
 658→        if (subscriptions && subscriptions.length > 0) {
 659→          console.log(
 660→            `[transaction.completed] Found ${subscriptions.length} unlinked subscription(s) - linking now`
 661→          );
 662→
 663→          for (const sub of subscriptions) {
 664→            await supabase
 665→              .from("subscriptions")
 666→              .update({
 667→                user_id: customerData.user_id,
 668→                updated_at: new Date().toISOString(),
 669→              })
 670→              .eq("id", sub.id);
 671→
 672→            if (sub.status === "active" && sub === subscriptions[0]) {
 673→              await supabase
 674→                .from("users")
 675→                .update({ current_subscription_id: sub.id })
 676→                .eq("id", customerData.user_id);
 677→            }
 678→
 679→            console.log(
 680→              `[transaction.completed] Linked subscription ${sub.id} to existing user ${customerData.user_id}`
 681→            );
 682→          }
 683→
 684→          // Send payment notification for the first active subscription
 685→          if (subscriptions.length > 0) {
 686→            await this.sendPaymentNotification(subscriptions[0].id, supabase);
 687→          }
 688→        } else {
 689→          console.log(
 690→            `[transaction.completed] No unlinked subscriptions found - subscription already linked by subscription.created event`
 691→          );
 692→        }
 693→
 694→        // IMPORTANT: Check for free trial REGARDLESS of whether subscription was just linked or already linked
 695→        // This handles the case where subscription.created came before transaction.completed
 696→        const priceId = eventData.data.items[0]?.price?.id;
 697→        if (priceId && this.isPriceWithTrial(priceId)) {
 698→          console.log(
 699→            `[transaction.completed] Free trial detected for priceId: ${priceId}`
 700→          );
 701→          await this.recordTrialUsage(customerId);
 702→        }
 703→      }
