 709→  /**
 710→   * Paddle Price ID로부터 플랜 이름 결정
 711→   * pricing-config.ts의 PADDLE_PRICE_TIERS를 단일 소스로 사용
 712→   */
 713→  private determinePlanFromPriceId(priceId: string | undefined): string {
 714→    if (!priceId) return "Basic";
 715→
 716→    // PADDLE_PRICE_TIERS를 순회하며 매칭
 717→    for (const tier of PADDLE_PRICE_TIERS) {
 718→      if (
 719→        tier.priceId.month === priceId ||
 720→        tier.priceId.year === priceId ||
 721→        tier.priceId.monthNoTrial === priceId ||
 722→        tier.priceId.yearNoTrial === priceId
 723→      ) {
 724→        return tier.name;
 725→      }
 726→    }
 727→
 728→    // 매칭 실패 시 기본값
 729→    return "Basic";
 730→  }
