 12→async function logWebhookEvent(
 13→  eventType: string,
 14→  payload: unknown,
 15→  status: "processed" | "error" = "processed",
 16→  errorMessage?: string
 17→) {
 18→  try {
 19→    const supabase = createServiceClient();
 20→    await supabase.from("webhook_events").insert({
 21→      event_type: eventType,
 22→      payload: payload as Record<string, unknown>,
 23→      status,
 24→      error_message: errorMessage,
 25→    });
 26→  } catch (e) {
 27→    console.error("[webhook-log] Failed to log event:", e);
 28→  }
 29→}
