 31→export async function processWebhookEvent(eventData: EventEntity) {
 32→  const eventType = eventData.eventType;
 33→  console.log(`[webhook] Processing: ${eventType}`);
 34→
 35→  try {
 36→    switch (eventType) {
 37→      case EventName.SubscriptionCreated:
 38→      case EventName.SubscriptionUpdated:
 39→      case EventName.SubscriptionCanceled:
 40→        await handleSubscriptionEvent(eventData);
 41→        break;
 42→      case EventName.CustomerCreated:
 43→      case EventName.CustomerUpdated:
 44→        await handleCustomerEvent(eventData);
 45→        break;
 46→      case EventName.TransactionCompleted:
 47→        await handleTransactionCompleted(eventData);
 48→        break;
 49→      default:
 50→        console.log(`[webhook] Unhandled event: ${eventType}`);
 51→    }
 52→    await logWebhookEvent(eventType, eventData.data);
 53→  } catch (error) {
 54→    await logWebhookEvent(
 55→      eventType,
 56→      eventData.data,
 57→      "error",
 58→      error instanceof Error ? error.message : "Unknown"
 59→    );
 60→    throw error; // Re-throw so webhook route returns 500 → Paddle retries
 61→  }
 62→}
