 64→function mapPaddleStatus(paddleStatus: string): string {
 65→  switch (paddleStatus) {
 66→    case "active":
 67→    case "trialing":
 68→    case "past_due": // Keep as active during payment retry period
 69→      return "active";
 70→    case "canceled":
 71→      return "canceled";
 72→    case "paused":
 73→      return "paused";
 74→    default:
 75→      return "inactive";
 76→  }
