 79→async function cancelDuplicateSubscriptions(
 80→  supabase: ReturnType<typeof createServiceClient>,
 81→  currentSubscriptionId: string,
 82→  paddleCustomerId: string,
 83→  userId: string | null
 84→) {
 85→  console.log(
 86→    `[duplicate-check] Checking for duplicates: current=${currentSubscriptionId}`
 87→  );
 88→
 89→  // Query for other active subscriptions
 90→  let query = supabase
 91→    .from("subscriptions")
 92→    .select("paddle_subscription_id, created_at")
 93→    .eq("status", "active")
 94→    .neq("paddle_subscription_id", currentSubscriptionId);
 95→
 96→  // Match by user_id if available, otherwise by customer_id
 97→  if (userId) {
 98→    query = query.eq("user_id", userId);
 99→  } else {
100→    query = query.eq("paddle_customer_id", paddleCustomerId);
101→  }
102→
103→  const { data: duplicates, error } = await query;
104→
105→  if (error) {
106→    console.error("[duplicate-check] Query failed:", error);
107→    throw error;
108→  }
109→
110→  if (!duplicates || duplicates.length === 0) {
111→    console.log("[duplicate-check] No duplicates found");
112→    return;
113→  }
114→
115→  console.log(
116→    `[duplicate-check] Found ${duplicates.length} duplicate(s), cancelling older ones`
117→  );
118→
119→  const paddle = getPaddleInstance();
120→
121→  for (const dup of duplicates) {
122→    try {
123→      console.log(
124→        `[duplicate-cancel] Cancelling ${dup.paddle_subscription_id}`
125→      );
126→      await paddle.subscriptions.cancel(dup.paddle_subscription_id, {
127→        effectiveFrom: "immediately",
128→      });
129→      console.log(
130→        `[duplicate-cancel] Successfully cancelled ${dup.paddle_subscription_id}`
131→      );
132→    } catch (cancelError) {
133→      console.error(
134→        `[duplicate-cancel] Failed to cancel ${dup.paddle_subscription_id}:`,
135→        cancelError
136→      );
137→      // Continue cancelling other duplicates even if one fails
138→    }
139→  }
140→}
141→
142→async function handleSubscriptionEvent(eventData: EventEntity) {
143→  const supabase = createServiceClient();
144→  const data = eventData.data as unknown as Record<string, unknown>;
145→  const paddleSubscriptionId = data.id as string;
146→  const paddleCustomerId = data.customerId as string;
147→
148→  const items = data.items as Array<{ price?: { id?: string } }> | undefined;
149→  const priceId = items?.[0]?.price?.id ?? null;
150→  const status = mapPaddleStatus(data.status as string);
151→
152→  console.log(
153→    `[subscription] ${eventData.eventType}: ${paddleSubscriptionId} customer=${paddleCustomerId} status=${status}`
154→  );
155→
156→  // Calculate ends_at
157→  let endsAt: string | null = null;
158→  const scheduledChange = data.scheduled_change as
159→    | { action?: string; effective_at?: string }
160→    | undefined;
161→  const currentBillingPeriod = data.currentBillingPeriod as
162→    | { endsAt?: string }
163→    | undefined;
164→
165→  if (scheduledChange?.action === "cancel") {
166→    endsAt = scheduledChange.effective_at || null;
167→  } else if (data.next_billed_at) {
168→    endsAt = data.next_billed_at as string;
169→  } else if (currentBillingPeriod?.endsAt) {
170→    endsAt = currentBillingPeriod.endsAt;
171→  }
172→
173→  // Try to find user_id from customers table
174→  const { data: customerData } = await supabase
175→    .from("customers")
176→    .select("user_id")
177→    .eq("customer_id", paddleCustomerId)
178→    .single();
179→
180→  const userId = customerData?.user_id || null;
181→
182→  // Idempotent upsert on paddle_subscription_id
183→  const { error } = await supabase.from("subscriptions").upsert(
184→    {
185→      user_id: userId,
186→      paddle_subscription_id: paddleSubscriptionId,
187→      paddle_customer_id: paddleCustomerId,
188→      paddle_price_id: priceId,
189→      status,
190→      starts_at: (data.startedAt as string) || new Date().toISOString(),
191→      ends_at: endsAt,
192→      canceled_at: status === "canceled" ? new Date().toISOString() : null,
193→      updated_at: new Date().toISOString(),
194→    },
195→    { onConflict: "paddle_subscription_id" }
196→  );
197→
198→  if (error) {
199→    console.error("[subscription] Upsert failed:", error);
200→    throw error;
201→  }
202→
203→  console.log(
204→    `[subscription] Upserted: ${paddleSubscriptionId} status=${status} user=${userId || "unlinked"}`
205→  );
206→
207→  // 구독 상태 변경에 따라 API 키 활성/비활성 처리
208→  // is_active 변경 시 notify_api_key_state_change 트리거가 api.hudy.co.kr 캐시 무효화
209→  if (userId) {
210→    const shouldBeActive = status === "active";
211→    const { error: keyError } = await supabase
212→      .from("api_keys")
213→      .update({ is_active: shouldBeActive, updated_at: new Date().toISOString() })
214→      .eq("user_id", userId);
215→
216→    if (keyError) {
217→      console.error(`[subscription] API key ${shouldBeActive ? "activate" : "deactivate"} failed:`, keyError);
218→    } else {
219→      console.log(`[subscription] API keys ${shouldBeActive ? "activated" : "deactivated"} for user ${userId}`);
220→    }
221→  }
222→
223→  // Auto-cancel duplicate subscriptions (only on creation)
224→  if (eventData.eventType === EventName.SubscriptionCreated) {
225→    try {
226→      await cancelDuplicateSubscriptions(
227→        supabase,
228→        paddleSubscriptionId,
229→        paddleCustomerId,
230→        userId
231→      );
232→    } catch (dupError) {
233→      console.error(
234→        "[subscription] Duplicate cancellation failed (non-fatal):",
235→        dupError
236→      );
237→      // Don't throw - let the main subscription creation succeed
238→    }
239→  }
240→}
241→
242→async function handleCustomerEvent(eventData: EventEntity) {
243→  const supabase = createServiceClient();
244→  const data = eventData.data as unknown as Record<string, unknown>;
245→  const customerId = data.id as string;
246→  const email = data.email as string;
247→
248→  console.log(
249→    `[customer] ${eventData.eventType}: ${customerId} email=${email}`
250→  );
251→
252→  // Use RPC function instead of listUsers()
253→  const { data: userId } = await supabase.rpc("get_user_id_by_email", {
254→    lookup_email: email,
255→  });
256→
257→  // Upsert customer record
258→  const { error } = await supabase.from("customers").upsert(
259→    {
260→      customer_id: customerId,
261→      email,
262→      user_id: userId || null,
263→      updated_at: new Date().toISOString(),
264→    },
265→    { onConflict: "customer_id" }
266→  );
267→
268→  if (error) {
269→    console.error("[customer] Upsert failed:", error);
270→    throw error;
271→  }
272→
273→  // If user found, link any orphaned subscriptions
274→  if (userId) {
275→    const { data: orphanedSubs } = await supabase
276→      .from("subscriptions")
277→      .select("id, status")
278→      .eq("paddle_customer_id", customerId)
279→      .is("user_id", null);
280→
281→    if (orphanedSubs && orphanedSubs.length > 0) {
282→      console.log(
283→        `[customer] Linking ${orphanedSubs.length} orphaned subscription(s) to user ${userId}`
284→      );
285→      for (const sub of orphanedSubs) {
286→        await supabase
287→          .from("subscriptions")
288→          .update({ user_id: userId, updated_at: new Date().toISOString() })
289→          .eq("id", sub.id);
290→      }
291→    }
292→  }
293→
294→  console.log(
295→    `[customer] Upserted: ${customerId} user=${userId || "not found"}`
296→  );
297→}
298→
299→async function handleTransactionCompleted(eventData: EventEntity) {
300→  const supabase = createServiceClient();
301→  const data = eventData.data as unknown as Record<string, unknown>;
302→  const customerId = data.customerId as string;
303→
304→  if (!customerId) {
305→    console.log("[transaction.completed] No customer_id");
306→    return;
307→  }
308→
309→  console.log(
310→    `[transaction.completed] Processing for customer ${customerId}`
311→  );
312→
313→  // 1. Get customer email from Paddle API (with DB fallback)
314→  let customerEmail: string | null = null;
315→
316→  try {
317→    const paddle = getPaddleInstance();
318→    const paddleCustomer = await paddle.customers.get(customerId);
319→    customerEmail = paddleCustomer.email;
320→  } catch {
321→    const { data: dbCustomer } = await supabase
322→      .from("customers")
323→      .select("email")
324→      .eq("customer_id", customerId)
325→      .single();
326→    customerEmail = dbCustomer?.email || null;
327→  }
328→
329→  if (!customerEmail) {
330→    console.error(
331→      `[transaction.completed] No email for customer ${customerId}`
332→    );
333→    throw new Error(`No email found for customer ${customerId}`);
334→  }
335→
336→  // 2. Upsert customer record using RPC instead of listUsers()
337→  const { data: userId } = await supabase.rpc("get_user_id_by_email", {
338→    lookup_email: customerEmail,
339→  });
340→
341→  const { error: customerUpsertError } = await supabase
342→    .from("customers")
343→    .upsert(
344→      {
345→        customer_id: customerId,
346→        email: customerEmail,
347→        user_id: userId || null,
348→        updated_at: new Date().toISOString(),
349→      },
350→      { onConflict: "customer_id" }
351→    );
352→
353→  if (customerUpsertError) {
354→    console.error(
355→      "[transaction.completed] Customer upsert failed:",
356→      customerUpsertError
357→    );
358→    throw customerUpsertError;
359→  }
360→
361→  // 3. Link orphaned subscriptions if user found
362→  if (userId) {
363→    const { data: orphanedSubs } = await supabase
364→      .from("subscriptions")
365→      .select("id, paddle_subscription_id, status")
366→      .eq("paddle_customer_id", customerId)
367→      .is("user_id", null);
368→
369→    if (orphanedSubs && orphanedSubs.length > 0) {
370→      console.log(
371→        `[transaction.completed] Linking ${orphanedSubs.length} subscription(s) to user ${userId}`
372→      );
373→
374→      for (const sub of orphanedSubs) {
375→        const { error: linkError } = await supabase
376→          .from("subscriptions")
377→          .update({ user_id: userId, updated_at: new Date().toISOString() })
378→          .eq("id", sub.id);
379→
380→        if (linkError) {
381→          console.error(
382→            `[transaction.completed] Failed to link subscription ${sub.id}:`,
383→            linkError
384→          );
385→          throw linkError;
386→        }
387→
388→        console.log(
389→          `[transaction.completed] Linked subscription ${sub.id} to user ${userId}`
390→        );
391→      }
392→    }
393→
394→    console.log(
395→      `[transaction.completed] Successfully processed for customer ${customerId} → user ${userId}`
396→    );
397→  } else {
398→    console.warn(
399→      `[transaction.completed] No user found for email ${customerEmail}`
400→    );
401→    throw new Error(`No user found for email ${customerEmail}`);
402→  }
403→}
