 6→export async function cancelSubscription(subscriptionId: string) {
 7→  const supabase = await createClient();
 8→  const { data: { user } } = await supabase.auth.getUser();
 9→
10→  if (!user) {
11→    return { error: "Unauthorized" };
12→  }
13→
14→  // Verify ownership
15→  const { data: subscription } = await supabase
16→    .from("subscriptions")
17→    .select("paddle_subscription_id")
18→    .eq("id", subscriptionId)
19→    .eq("user_id", user.id)
20→    .single();
21→
22→  if (!subscription?.paddle_subscription_id) {
23→    return { error: "Subscription not found" };
24→  }
25→
26→  try {
27→    const paddle = getPaddleInstance();
28→    await paddle.subscriptions.cancel(subscription.paddle_subscription_id, {
29→      effectiveFrom: "next_billing_period",
30→    });
31→
32→    await supabase
33→      .from("subscriptions")
34→      .update({
35→        canceled_at: new Date().toISOString(),
36→        updated_at: new Date().toISOString(),
37→      })
38→      .eq("id", subscriptionId);
39→
40→    return { success: true };
41→  } catch (error) {
42→    console.error("Failed to cancel subscription:", error);
43→    return { error: "Failed to cancel subscription" };
44→  }
