 1→import { NextRequest } from "next/server";
 2→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 3→import { processWebhookEvent } from "@/lib/paddle/process-webhook";
 4→
 5→export async function POST(request: NextRequest) {
 6→  const signature = request.headers.get("paddle-signature") || "";
 7→  const rawRequestBody = await request.text();
 8→  const privateKey = process.env.PADDLE_NOTIFICATION_WEBHOOK_SECRET || "";
 9→
10→  try {
11→    if (!signature || !rawRequestBody) {
12→      return Response.json(
13→        { error: "Missing signature or body" },
14→        { status: 400 }
15→      );
16→    }
17→
18→    if (!privateKey) {
19→      console.error("PADDLE_NOTIFICATION_WEBHOOK_SECRET not configured");
20→      return Response.json(
21→        { error: "Webhook secret not configured" },
22→        { status: 500 }
23→      );
24→    }
25→
26→    const paddle = getPaddleInstance();
27→    const eventData = await paddle.webhooks.unmarshal(
28→      rawRequestBody,
29→      privateKey,
30→      signature
31→    );
32→
33→    if (!eventData) {
34→      return Response.json(
35→        { error: "Failed to parse webhook" },
36→        { status: 400 }
37→      );
38→    }
39→
40→    const eventName = eventData.eventType ?? "Unknown";
41→    console.log(`[webhook] Received: ${eventName}`);
42→
43→    await processWebhookEvent(eventData);
44→
45→    console.log(`[webhook] Processed: ${eventName}`);
46→    return Response.json({ status: 200, eventName });
47→  } catch (e) {
48→    console.error("[webhook] Error:", e);
49→    return Response.json(
50→      {
51→        error: "Internal server error",
52→        details: e instanceof Error ? e.message : "Unknown",
53→      },
54→      { status: 500 }
55→    );
56→  }
57→}
