 1→import { AsyncLocalStorage } from "node:async_hooks";
 2→import { createAdminClient } from "@/lib/supabase/admin";
 3→
 4→export interface AuthContext {
 5→  apiKeyId: string;
 6→  userId: string;
 7→  apiKey: string;
 8→}
 9→
10→export const authStore = new AsyncLocalStorage<AuthContext>();
11→
12→export function getAuthContext(): AuthContext {
13→  const ctx = authStore.getStore();
14→  if (!ctx) throw new Error("No auth context available");
15→  return ctx;
16→}
17→
18→export type ValidateResult =
19→  | { ok: true; context: AuthContext }
20→  | { ok: false; reason: "invalid_key" | "no_subscription" };
21→
22→export async function validateApiKey(
23→  apiKey: string | null
24→): Promise<ValidateResult> {
25→  if (!apiKey) return { ok: false, reason: "invalid_key" };
26→
27→  const supabase = createAdminClient();
28→  const { data, error } = await supabase
29→    .from("api_keys")
30→    .select("id, user_id, is_active")
31→    .eq("key", apiKey)
32→    .single();
33→
34→  if (error || !data || !data.is_active)
35→    return { ok: false, reason: "invalid_key" };
36→
37→  // 구독 상태 확인 - active 구독이 없으면 API 사용 불가
38→  const { data: subscription } = await supabase
39→    .from("subscriptions")
40→    .select("id")
41→    .eq("user_id", data.user_id)
42→    .eq("status", "active")
43→    .limit(1)
44→    .maybeSingle();
45→
46→  if (!subscription) return { ok: false, reason: "no_subscription" };
47→
48→  return {
49→    ok: true,
50→    context: { apiKeyId: data.id, userId: data.user_id, apiKey },
51→  };
52→}
