  1→import { EventEntity, EventName } from "@paddle/paddle-node-sdk";
  2→import { createClient as createSupabaseClient } from "@supabase/supabase-js";
  3→import { getPaddleInstance } from "./get-paddle-instance";
  4→
  5→function createServiceClient() {
  6→  return createSupabaseClient(
  7→    process.env.NEXT_PUBLIC_SUPABASE_URL!,
  8→    process.env.SUPABASE_SECRET_KEY!
  9→  );
 10→}
 11→
 12→async function logWebhookEvent(
 13→  eventType: string,
 14→  payload: unknown,
 15→  status: "processed" | "error" = "processed",
 16→  errorMessage?: string
 17→) {
 18→  try {
 19→    const supabase = createServiceClient();
 20→    await supabase.from("webhook_events").insert({
 21→      event_type: eventType,
 22→      payload: payload as Record<string, unknown>,
 23→      status,
 24→      error_message: errorMessage,
 25→    });
 26→  } catch (e) {
 27→    console.error("[webhook-log] Failed to log event:", e);
 28→  }
 29→}
 30→
 31→export async function processWebhookEvent(eventData: EventEntity) {
 32→  const eventType = eventData.eventType;
 33→  console.log(`[webhook] Processing: ${eventType}`);
 34→
 35→  try {
 36→    switch (eventType) {
 37→      case EventName.SubscriptionCreated:
 38→      case EventName.SubscriptionUpdated:
 39→      case EventName.SubscriptionCanceled:
 40→        await handleSubscriptionEvent(eventData);
 41→        break;
 42→      case EventName.CustomerCreated:
 43→      case EventName.CustomerUpdated:
 44→        await handleCustomerEvent(eventData);
 45→        break;
 46→      case EventName.TransactionCompleted:
 47→        await handleTransactionCompleted(eventData);
 48→        break;
 49→      default:
 50→        console.log(`[webhook] Unhandled event: ${eventType}`);
 51→    }
 52→    await logWebhookEvent(eventType, eventData.data);
 53→  } catch (error) {
 54→    await logWebhookEvent(
 55→      eventType,
 56→      eventData.data,
 57→      "error",
 58→      error instanceof Error ? error.message : "Unknown"
 59→    );
 60→    throw error; // Re-throw so webhook route returns 500 → Paddle retries
 61→  }
 62→}
 63→
 64→function mapPaddleStatus(paddleStatus: string): string {
 65→  switch (paddleStatus) {
 66→    case "active":
