13→}
14→
15→impl UsageBatcher {
16→    pub fn new() -> Self {
17→        Self {
18→            counters: Arc::new(RwLock::new(HashMap::new())),
19→        }
20→    }
21→
22→    pub async fn increment(&self, api_key_id: Uuid) {
23→        let mut counters = self.counters.write().await;
24→        *counters.entry(api_key_id).or_insert(0) += 1;
25→    }
26→
27→    pub fn start_flush_worker(self: Arc<Self>, pool: PgPool, interval: Duration) {
28→        tokio::spawn(async move {
29→            let mut ticker = tokio::time::interval(interval);
30→            loop {
31→                ticker.tick().await;
32→                if let Err(e) = self.flush(&pool).await {
33→                    tracing::warn!("UsageBatcher flush failed: {e}");
34→                }
35→            }
36→        });
37→    }
38→
39→    async fn flush(&self, pool: &PgPool) -> Result<(), AppError> {
40→        let batch = {
41→            let mut counters = self.counters.write().await;
42→            if counters.is_empty() {
43→                return Ok(());
44→            }
45→            std::mem::take(&mut *counters)
46→        };
47→
48→        let today = chrono::Utc::now().date_naive();
49→
50→        for (api_key_id, delta) in batch {
51→            sqlx::query(
52→                r#"
53→                INSERT INTO api_usage_daily (api_key_id, date, call_count, updated_at)
54→                VALUES ($1, $2, $3, NOW())
55→                ON CONFLICT (api_key_id, date)
56→                DO UPDATE SET call_count = api_usage_daily.call_count + $3, updated_at = NOW()
57→                "#,
58→            )
59→            .bind(api_key_id)
60→            .bind(today)
61→            .bind(delta)
62→            .execute(pool)
63→            .await?;
64→        }
65→
66→        Ok(())
67→    }
68→}
