47→    type Error = Error;
48→    type Future = LocalBoxFuture<'static, Result<Self::Response, Self::Error>>;
49→
50→    forward_ready!(service);
51→
52→    fn call(&self, req: ServiceRequest) -> Self::Future {
53→        let service = Rc::clone(&self.service);
54→
55→        Box::pin(async move {
56→            // Skip auth for health and admin endpoints
57→            if req.path() == "/v1/health" || req.path().starts_with("/v1/admin/") {
58→                return service.call(req).await;
59→            }
60→
61→            let t0 = std::time::Instant::now();
62→
63→            let api_key = req
64→                .headers()
65→                .get("x-api-key")
66→                .and_then(|v| v.to_str().ok())
67→                .map(str::trim)
68→                .filter(|k| !k.is_empty())
69→                .ok_or_else(|| AppError::Unauthorized("Missing x-api-key header".to_string()))?;
70→
71→            let pool = req
72→                .app_data::<web::Data<PgPool>>()
73→                .ok_or_else(|| AppError::Internal("Missing DB pool".to_string()))?;
74→            let cache = req
75→                .app_data::<web::Data<Arc<ApiKeyStateCache>>>()
76→                .ok_or_else(|| AppError::Internal("Missing API key state cache".to_string()))?;
77→
78→            let state = cache.get_or_load(pool.get_ref(), api_key).await?;
79→            tracing::info!(us = t0.elapsed().as_micros(), "PERF api_key_auth");
80→
81→            if !state.is_active {
82→                return Err(AppError::Unauthorized("API key is deactivated".to_string()).into());
83→            }
84→
85→            req.extensions_mut().insert(AuthContext {
86→                api_key_id: state.api_key_id,
87→                user_id: state.user_id,
88→            });
89→
90→            service.call(req).await
