 10→#[derive(Clone, Debug)]
 11→pub struct ApiKeyState {
 12→    pub api_key_id: Uuid,
 13→    pub user_id: Uuid,
 14→    pub is_active: bool,
 15→}
 16→
 17→#[derive(Clone, Debug)]
 18→struct CacheEntry {
 19→    state: ApiKeyState,
 20→    cached_at: Instant,
 21→}
 22→
 23→#[derive(Debug, FromRow)]
 24→struct ApiKeyRow {
 25→    id: Uuid,
 26→    user_id: Uuid,
 27→    is_active: bool,
 28→}
 29→
 30→pub struct ApiKeyStateCache {
 31→    ttl: Duration,
 32→    inner: RwLock<HashMap<String, CacheEntry>>, // key_string -> entry
 33→    reverse: RwLock<HashMap<Uuid, String>>,     // api_key_id -> key_string
 34→}
 35→
 36→impl ApiKeyStateCache {
 37→    pub fn new(ttl: Duration) -> Self {
 38→        Self {
 39→            ttl,
 40→            inner: RwLock::new(HashMap::new()),
 41→            reverse: RwLock::new(HashMap::new()),
 42→        }
 43→    }
 44→
 45→    /// API key 문자열로 조회. 캐시 미스 시 DB에서 로드.
 46→    pub async fn get_or_load(&self, pool: &PgPool, key: &str) -> Result<ApiKeyState, AppError> {
 47→        // 캐시 히트 체크
 48→        {
 49→            let guard = self.inner.read().await;
 50→            if let Some(entry) = guard.get(key)
 51→                && entry.cached_at.elapsed() < self.ttl
 52→            {
 53→                return Ok(entry.state.clone());
 54→            }
 55→        }
 56→
 57→        // DB 조회
 58→        let row = sqlx::query_as::<_, ApiKeyRow>(
 59→            "SELECT id, user_id, is_active FROM api_keys WHERE key = $1",
 60→        )
 61→        .bind(key)
 62→        .fetch_optional(pool)
 63→        .await?;
 64→
 65→        let state = match row {
 66→            Some(r) => ApiKeyState {
 67→                api_key_id: r.id,
 68→                user_id: r.user_id,
 69→                is_active: r.is_active,
 70→            },
 71→            None => return Err(AppError::Unauthorized("Invalid API key".to_string())),
 72→        };
 73→
 74→        // 캐시 저장 + 역인덱스 업데이트
 75→        self.upsert(key, &state).await;
 76→        Ok(state)
 77→    }
 78→
 79→    async fn upsert(&self, key: &str, state: &ApiKeyState) {
 80→        {
 81→            let mut guard = self.inner.write().await;
 82→            guard.insert(
 83→                key.to_string(),
 84→                CacheEntry {
 85→                    state: state.clone(),
 86→                    cached_at: Instant::now(),
 87→                },
 88→            );
 89→        }
 90→        {
 91→            let mut guard = self.reverse.write().await;
 92→            guard.insert(state.api_key_id, key.to_string());
 93→        }
 94→    }
 95→
 96→    /// api_key_id로 캐시 무효화 (admin 엔드포인트용)
 97→    pub async fn invalidate_by_id(&self, api_key_id: Uuid) {
 98→        let key_string = {
 99→            let mut guard = self.reverse.write().await;
100→            guard.remove(&api_key_id)
101→        };
102→        if let Some(key) = key_string {
103→            let mut guard = self.inner.write().await;
104→            guard.remove(&key);
105→        }
106→    }
107→}
