import type { Metadata } from "next"
import { Navbar } from "@/components/landing/navbar"
import { HeroSection } from "@/components/landing/hero-section"
import { StatsSection } from "@/components/landing/stats-section"
import { GlobeSection } from "@/components/landing/globe-section"
import { FeaturesSection } from "@/components/landing/features-section"
import { PricingSection } from "@/components/landing/pricing-section"
import { DocsSection } from "@/components/landing/docs-section"
import { FaqSection } from "@/components/landing/faq-section"
import { faqs } from "@/lib/faq-data"
import { Footer } from "@/components/landing/footer"

export const metadata: Metadata = {
  title: "HuDy - 대한민국 공휴일 API | 영업일 계산 · 커스텀 공휴일 · MCP 서버",
  description:
    "대한민국 공휴일 조회, 영업일 계산, 커스텀 공휴일 관리를 위한 REST API. Node.js · Python SDK와 MCP 서버를 지원합니다. 한 줄의 API 호출로 공휴일 데이터를 통합하세요.",
  alternates: {
    canonical: "https://www.hudy.co.kr",
  },
}

function JsonLd() {
  const structuredData = {
    '@context': 'https://schema.org',
    '@graph': [
      {
        '@type': 'WebSite',
        name: 'HuDy',
        url: 'https://www.hudy.co.kr',
        description: '대한민국 공휴일 조회, 영업일 계산, 커스텀 공휴일 관리를 위한 REST API.',
        inLanguage: 'ko',
        potentialAction: {
          '@type': 'SearchAction',
          target: 'https://www.hudy.co.kr/#docs',
          'query-input': 'required name=search_term_string',
        },
      },
      {
        '@type': 'SoftwareApplication',
        name: 'HuDy API',
        applicationCategory: 'DeveloperApplication',
        operatingSystem: 'Any',
        url: 'https://www.hudy.co.kr',
        description: '대한민국 공휴일 조회 및 영업일 계산 REST API. 커스텀 공휴일 등록, MCP 서버 지원.',
        offers: {
          '@type': 'Offer',
          price: '3',
          priceCurrency: 'USD',
          description: '월 $3 단일 요금제, 30일 무료 체험',
          priceValidUntil: '2026-12-31',
        },
        featureList: [
          '대한민국 법정 공휴일 조회',
          '대체 공휴일, 임시 공휴일 포함',
          '영업일 계산 (영업일 수 계산, 영업일 더하기/빼기)',
          '커스텀 공휴일 등록 및 관리',
          'MCP 서버 지원 (Claude, Cursor 등)',
          'Node.js / Python 공식 SDK',
          'REST API / JSON 응답',
          '100ms 이내 응답 속도',
        ],
        aggregateRating: {
          '@type': 'AggregateRating',
          ratingValue: '4.8',
          ratingCount: '50',
        },
      },
      {
        '@type': 'Organization',
        name: 'HuDy',
        url: 'https://www.hudy.co.kr',
        logo: 'https://www.hudy.co.kr/hudy-icon.webp',
        sameAs: [
          'https://www.npmjs.com/package/@hudy-sdk/sdk',
          'https://pypi.org/project/hudy-sdk/',
        ],
      },
      {
        '@type': 'BreadcrumbList',
        itemListElement: [
          {
            '@type': 'ListItem',
            position: 1,
            name: '홈',
            item: 'https://www.hudy.co.kr',
          },
        ],
      },
      {
        '@type': 'FAQPage',
        mainEntity: faqs.map((faq) => ({
          '@type': 'Question',
          name: faq.question,
          acceptedAnswer: {
            '@type': 'Answer',
            text: faq.answer,
          },
        })),
      },
    ],
  }

  return (
    <script
      type="application/ld+json"
      dangerouslySetInnerHTML={{ __html: JSON.stringify(structuredData) }}
    />
  )
}

export default function Page() {
  return (
    <>
      <JsonLd />
      <main>
        <Navbar />
        <HeroSection />
        <StatsSection />
        <GlobeSection />
        <FeaturesSection />
        <PricingSection />
        <DocsSection />
        <FaqSection />
        <Footer />
      </main>
    </>
  )
}

import { Calendar, CalendarCheck, Code2, Layers, Package, Zap } from "lucide-react"

const features = [
  {
    icon: Calendar,
    title: "공휴일 조회",
    description: "대한민국 법정 공휴일, 대체 공휴일, 임시 공휴일까지 모두 정확하게 조회할 수 있습니다.",
  },
  {
    icon: Layers,
    title: "커스텀 공휴일",
    description: "회사 창립기념일, 사내 행사일 등 나만의 커스텀 공휴일을 등록하고 API로 조회하세요.",
  },
  {
    icon: CalendarCheck,
    title: "영업일 관리",
    description: "특정 날짜의 영업일 여부 확인, 기간 내 영업일 수 계산, N영업일 후/전 날짜 계산을 지원합니다.",
  },
  {
    icon: Package,
    title: "공식 SDK",
    description: "Node.js와 Python SDK를 제공합니다. npm install 또는 pip install 한 줄이면 바로 시작.",
  },
  {
    icon: Zap,
    title: "빠른 응답 속도",
    description: "어디서나 100ms 이내 응답. 안정적인 인프라로 서비스합니다.",
  },
  {
    icon: Code2,
    title: "개발자 친화적",
    description: "RESTful API, JSON 응답, MCP 지원. 어떤 언어에서든, AI Agent에서도 쉽게 연동할 수 있습니다.",
  },
]

export function FeaturesSection() {
  return (
    <section id="features" className="px-6 py-24">
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <p className="mb-3 text-sm font-medium uppercase tracking-widest text-primary">Features</p>
          <h2 className="text-balance text-3xl font-bold tracking-tight text-foreground md:text-4xl">
            {"개발자를 위해 설계된 API"}
          </h2>
          <p className="mx-auto mt-4 max-w-xl text-pretty text-muted-foreground">
            {"단순한 공휴일 조회를 넘어, 비즈니스에 필요한 모든 휴일 관련 기능을 제공합니다."}
          </p>
        </div>

        <div className="grid gap-6 md:grid-cols-2">
          {features.map((feature) => (
            <div
              key={feature.title}
              className="group rounded-xl border border-border bg-card p-6 transition-colors hover:border-primary/30 hover:bg-card/80"
            >
              <div className="mb-4 flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
                <feature.icon className="h-5 w-5 text-primary" />
              </div>
              <h3 className="mb-2 text-lg font-semibold text-foreground">{feature.title}</h3>
              <p className="text-sm leading-relaxed text-muted-foreground">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}

export const faqs = [
  {
    question: "HuDy API는 무료로 사용할 수 있나요?",
    answer:
      "HuDy는 월 $3의 단일 요금제로 운영되며, 30일 무료 체험을 제공합니다. 무료 체험 기간 동안 모든 기능을 제한 없이 사용할 수 있습니다.",
  },
  {
    question: "어떤 공휴일 데이터를 제공하나요?",
    answer:
      "대한민국 법정 공휴일, 대체 공휴일, 임시 공휴일을 모두 포함합니다. 또한 회사 창립기념일 등 커스텀 공휴일을 직접 등록하여 API로 조회할 수 있습니다.",
  },
  {
    question: "영업일 계산은 어떻게 하나요?",
    answer:
      "HuDy API의 영업일 계산 엔드포인트를 사용하면 특정 날짜의 영업일 여부 확인, 두 날짜 사이의 영업일 수 계산, N영업일 후/전 날짜 계산을 할 수 있습니다. 주말과 공휴일을 자동으로 제외합니다.",
  },
  {
    question: "SDK는 어떤 언어를 지원하나요?",
    answer:
      "현재 Node.js/TypeScript(@hudy-sdk/sdk)와 Python(hudy-sdk)을 공식 지원합니다. npm install 또는 pip install 한 줄로 바로 사용할 수 있습니다.",
  },
  {
    question: "MCP 서버란 무엇인가요?",
    answer:
      "MCP(Model Context Protocol) 서버를 통해 Claude Desktop, Cursor 등 AI 도구에서 공휴일 조회와 영업일 계산 기능을 바로 사용할 수 있습니다. 설정 파일에 HuDy MCP 서버 URL만 추가하면 됩니다.",
  },
  {
    question: "API 응답 속도는 얼마나 빠른가요?",
    answer:
      "HuDy API는 전 세계 어디서나 100ms 이내의 응답 속도를 보장합니다. 안정적인 인프라 위에서 서비스되며, 99.9% 이상의 가동률을 유지합니다.",
  },
]

