"use client"

import { useState, useEffect, useCallback } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog"
import { Copy, Eye, EyeOff, Key, Plus, RefreshCw, Power } from "lucide-react"
import { createClient } from "@/lib/supabase/client"
import { toast } from "sonner"

interface ApiKey {
  id: string
  name: string
  key: string
  is_active: boolean
  created_at: string
  updated_at: string
}

export default function ApiKeysPage() {
  const [keys, setKeys] = useState<ApiKey[]>([])
  const [visibleKeys, setVisibleKeys] = useState<Set<string>>(new Set())
  const [showCreate, setShowCreate] = useState(false)
  const [newKeyName, setNewKeyName] = useState("")
  const [loading, setLoading] = useState(true)
  const [creating, setCreating] = useState(false)
  const [recyclingIds, setRecyclingIds] = useState<Set<string>>(new Set())
  const [showReplaceDialog, setShowReplaceDialog] = useState(false)

  const fetchKeys = useCallback(async () => {
    setLoading(true)
    const supabase = createClient()
    const { data, error } = await supabase
      .from("api_keys")
      .select("*")
      .order("created_at", { ascending: false })

    if (error) {
      toast.error("API 키 로드 중 오류가 발생했습니다.")
      console.error(error)
    } else {
      setKeys(data || [])
    }
    setLoading(false)
  }, [])

  useEffect(() => {
    fetchKeys()
  }, [fetchKeys])

  const generateKey = () => {
    return (
      "hd_live_" +
      Array.from(crypto.getRandomValues(new Uint8Array(24)))
        .map((b) => b.toString(16).padStart(2, "0"))
        .join("")
        .slice(0, 32)
    )
  }

  const toggleVisibility = (id: string) => {
    setVisibleKeys((prev) => {
      const next = new Set(prev)
      if (next.has(id)) {
        next.delete(id)
      } else {
        next.add(id)
      }
      return next
    })
  }

  const maskValue = (value: string) => {
    if (value.length <= 16) {
      return `${value.slice(0, 4)}${"*".repeat(8)}${value.slice(-4)}`
    }
    return `${value.slice(0, 12)}${"*".repeat(20)}${value.slice(-6)}`
  }

  const copyKey = async (apiKey: ApiKey) => {
    await navigator.clipboard.writeText(apiKey.key)
    toast.success("API 키가 클립보드에 복사되었습니다.")
  }

  const createKey = async () => {
    if (!newKeyName.trim()) return

    // Check if keys already exist - show confirmation dialog
    if (keys.length > 0) {
      setShowReplaceDialog(true)
      return
    }

    await performCreateKey()
  }

  const performCreateKey = async () => {
    setCreating(true)

    const supabase = createClient()
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser()

    if (userError || !user) {
      toast.error("인증 정보를 확인할 수 없습니다.")
      setCreating(false)
      return
    }

    // Delete all existing keys first
    if (keys.length > 0) {
      const { error: deleteError } = await supabase
        .from("api_keys")
        .delete()
        .eq("user_id", user.id)

      if (deleteError) {
        toast.error("기존 키 삭제 중 오류가 발생했습니다.")
        console.error(deleteError)
        setCreating(false)
        setShowReplaceDialog(false)
        return
      }
    }

    const { error } = await supabase
      .from("api_keys")
      .insert({
        user_id: user.id,
        key: generateKey(),
        name: newKeyName.trim(),
      })

    if (error) {
      toast.error("API 키 생성 중 오류가 발생했습니다.")
      console.error(error)
      setCreating(false)
      setShowReplaceDialog(false)
      return
    }

    toast.success("API 키가 생성되었습니다.")
    await fetchKeys()
    setNewKeyName("")
    setShowCreate(false)
    setCreating(false)
    setShowReplaceDialog(false)
  }

  const recycleKey = async (id: string) => {
    setRecyclingIds((prev) => new Set(prev).add(id))

    const supabase = createClient()
    const { error } = await supabase
      .from("api_keys")
      .update({ key: generateKey() })
      .eq("id", id)

    if (error) {
      toast.error("API 키 재생성 중 오류가 발생했습니다.")
      console.error(error)
    } else {
      toast.success("API 키가 재생성되었습니다. 기존 키는 더 이상 사용할 수 없습니다.")
      setVisibleKeys((prev) => {
        const next = new Set(prev)
        next.delete(id)
        return next
      })
      await fetchKeys()
    }

    setRecyclingIds((prev) => {
      const next = new Set(prev)
      next.delete(id)
      return next
    })
  }

  const toggleActive = async (id: string, currentActive: boolean) => {
    const supabase = createClient()
    const { error } = await supabase
      .from("api_keys")
      .update({ is_active: !currentActive })
      .eq("id", id)

    if (error) {
      toast.error("상태 변경 중 오류가 발생했습니다.")
      console.error(error)
    } else {
      toast.success(currentActive ? "API 키가 비활성화되었습니다." : "API 키가 활성화되었습니다.")
      await fetchKeys()
    }
  }

  const deleteKey = async (id: string) => {
    const supabase = createClient()
    const { error } = await supabase.from("api_keys").delete().eq("id", id)

    if (error) {
      toast.error("API 키 삭제 중 오류가 발생했습니다.")
      console.error(error)
    } else {
      toast.success("API 키가 삭제되었습니다.")
      await fetchKeys()
    }
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("ko-KR", {
      year: "numeric",
      month: "long",
      day: "numeric",
    })
  }

