 1→import { Environment, Paddle } from "@paddle/paddle-node-sdk";
 2→
 3→let paddleInstance: Paddle | null = null;
 4→
 5→export function getPaddleInstance(): Paddle {
 6→  if (!paddleInstance) {
 7→    const apiKey = process.env.PADDLE_API_KEY;
 8→
 9→    if (!apiKey) {
10→      throw new Error("PADDLE_API_KEY environment variable is not set");
11→    }
12→
13→    const environment =
14→      process.env.NEXT_PUBLIC_PADDLE_ENV === "sandbox"
15→        ? Environment.sandbox
16→        : Environment.production;
17→
18→    paddleInstance = new Paddle(apiKey, { environment });
19→  }
20→
21→  return paddleInstance;
22→}
