 864→  /**
 865→   * Handle credit purchase from transaction.completed event
 866→   * Adds credits to user's balance based on custom_data
 867→   * Uses the same customer lookup logic as subscription purchases
 868→   */
 869→  private async handleCreditPurchase(
 870→    eventData: TransactionCompletedEvent,
 871→    customData: CreditPurchaseCustomData
 872→  ) {
 873→    const supabase = createServiceSupabase();
 874→
 875→    try {
 876→      const quantity = parseInt(customData.quantity, 10);
 877→      const transactionId = eventData.data.id;
 878→      const customerId = eventData.data.customerId;
 879→
 880→      if (!customerId) {
 881→        console.error("[credit-purchase] No customer_id in event");
 882→        return;
 883→      }
 884→
 885→      if (isNaN(quantity) || quantity <= 0) {
 886→        console.error("[credit-purchase] Invalid quantity:", customData.quantity);
 887→        return;
 888→      }
 889→
 890→      console.log(
 891→        `[credit-purchase] Processing credit purchase: ${quantity} credits for customer ${customerId}`
 892→      );
 893→
 894→      // Check for duplicate transaction
 895→      const { data: existingTransaction } = await supabase
 896→        .from("credit_transactions")
 897→        .select("id")
 898→        .eq("paddle_transaction_id", transactionId)
 899→        .single();
 900→
 901→      if (existingTransaction) {
 902→        console.log(
 903→          `[credit-purchase] Transaction ${transactionId} already processed, skipping`
 904→        );
 905→        return;
 906→      }
 907→
 908→      // 1. Find user_id from customer (same logic as subscription)
 909→      let userId: string | null = null;
 910→
 911→      // 1a. Try to get user_id from existing customer record
 912→      const { data: customerData } = await supabase
 913→        .from("customers")
 914→        .select("user_id")
 915→        .eq("customer_id", customerId)
 916→        .single();
 917→
 918→      if (customerData?.user_id) {
 919→        userId = customerData.user_id;
 920→        console.log(`[credit-purchase] Found user ${userId} from existing customer record`);
 921→      } else {
 922→        // 1b. Customer doesn't exist or not linked - fetch from Paddle API and find user by email
 923→        console.log(`[credit-purchase] Customer not found in DB, fetching from Paddle API...`);
 924→
 925→        const paddle = getPaddleInstance();
 926→        let customerEmail: string | null = null;
 927→
 928→        try {
 929→          const paddleCustomer = await paddle.customers.get(customerId);
 930→          customerEmail = paddleCustomer.email;
 931→          console.log(`[credit-purchase] Fetched customer email from Paddle: ${customerEmail}`);
 932→        } catch (apiError) {
 933→          console.error(`[credit-purchase] Failed to fetch customer from Paddle API:`, apiError);
 934→          return;
 935→        }
 936→
 937→        if (!customerEmail) {
 938→          console.error(`[credit-purchase] No email available for customer ${customerId}`);
 939→          return;
 940→        }
 941→
 942→        // Find user by email
 943→        const {
 944→          data: { users },
 945→          error: listError,
 946→        } = await supabase.auth.admin.listUsers();
 947→
 948→        if (listError) {
 949→          console.error(`[credit-purchase] Failed to list users:`, listError);
 950→          return;
 951→        }
 952→
 953→        const user = users?.find((u) => u.email === customerEmail);
 954→
 955→        if (!user) {
 956→          console.error(
 957→            `[credit-purchase] No user found with email ${customerEmail}`
 958→          );
 959→          return;
 960→        }
 961→
 962→        userId = user.id;
 963→        console.log(`[credit-purchase] Found user ${userId} by email ${customerEmail}`);
 964→
 965→        // Create or update customer record
 966→        await supabase
 967→          .from("customers")
 968→          .upsert({
 969→            customer_id: customerId,
 970→            email: customerEmail,
 971→            user_id: userId,
 972→          });
 973→
 974→        console.log(`[credit-purchase] Created/updated customer record for ${customerId}`);
 975→      }
 976→
 977→      if (!userId) {
 978→        console.error("[credit-purchase] Could not determine user_id");
 979→        return;
 980→      }
 981→
 982→      // 2. Record transaction
 983→      const { error: transactionError } = await supabase
 984→        .from("credit_transactions")
 985→        .insert({
 986→          user_id: userId,
 987→          transaction_type: "purchase",
 988→          create_credits: quantity,
 989→          publish_credits: quantity,
 990→          paddle_transaction_id: transactionId,
 991→        });
 992→
 993→      if (transactionError) {
 994→        console.error("[credit-purchase] Failed to record transaction:", transactionError);
 995→        throw transactionError;
 996→      }
 997→
 998→      // 3. Update balance (upsert)
 999→      const { data: existing } = await supabase
1000→        .from("credit_balance")
1001→        .select("create_credits, publish_credits")
1002→        .eq("user_id", userId)
1003→        .single();
1004→
1005→      const newCreateCredits = (existing?.create_credits || 0) + quantity;
1006→      const newPublishCredits = (existing?.publish_credits || 0) + quantity;
1007→
1008→      const { error: balanceError } = await supabase
1009→        .from("credit_balance")
1010→        .upsert(
1011→          {
1012→            user_id: userId,
1013→            create_credits: newCreateCredits,
1014→            publish_credits: newPublishCredits,
1015→            updated_at: new Date().toISOString(),
1016→          },
1017→          { onConflict: "user_id" }
1018→        );
1019→
1020→      if (balanceError) {
1021→        console.error("[credit-purchase] Failed to update balance:", balanceError);
1022→        throw balanceError;
1023→      }
1024→
1025→      console.log(
1026→        `[credit-purchase] ✅ Successfully added ${quantity} credits for user ${userId}. ` +
1027→        `New balance: create=${newCreateCredits}, publish=${newPublishCredits}`
1028→      );
1029→
1030→      // 4. Send notification (optional)
1031→      await this.sendCreditPurchaseNotification(quantity, userId);
1032→    } catch (error) {
1033→      console.error("[credit-purchase] Error processing credit purchase:", error);
1034→      throw error;
1035→    }
1036→  }
