 1→"use server";
 2→
 3→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 4→import { getCustomerId } from "@/lib/paddle/get-customer-id";
 5→import { parseSDKResponse } from "@/lib/paddle/data-helpers";
 6→import type { SubscriptionEffectiveFrom } from "@paddle/paddle-node-sdk/dist/types/enums/subscription/subscription-effective-from";
 7→
 8→export async function cancelSubscription(
 9→  subscriptionId: string,
10→  effectiveFrom: SubscriptionEffectiveFrom = "next_billing_period"
11→): Promise<{ ok: boolean; error?: string; status?: string }> {
12→  try {
13→    const customerId = await getCustomerId();
14→    if (!customerId) {
15→      return { ok: false, error: "Missing customer context" };
16→    }
17→
18→    const paddle = getPaddleInstance();
19→
20→    // Verify the subscription belongs to the current customer
21→    const sub = await paddle.subscriptions.get(subscriptionId);
22→    const subJson = parseSDKResponse(sub);
23→    if (subJson.customerId !== customerId) {
24→      return { ok: false, error: "Unauthorized subscription access" };
25→    }
26→
27→    const canceled = await paddle.subscriptions.cancel(subscriptionId, {
28→      effectiveFrom,
29→    });
30→    const canceledJson = parseSDKResponse(canceled);
31→
32→    return { ok: true, status: canceledJson.status };
33→  } catch (e: any) {
34→    console.error("Error canceling subscription:", e);
35→    const message = e?.message || "Failed to cancel subscription";
36→    return { ok: false, error: message };
37→  }
38→}
