21→type TranslationFunction = (key: string, fallback?: string) => string;
22→
23→export function formatBillingCycle(
24→  { frequency, interval }: CheckoutEventsTimePeriod,
25→  t: TranslationFunction
26→) {
27→  if (frequency === 1) {
28→    return t(`checkout.billing.${interval}ly`);
29→  } else {
30→    return `${t("checkout.billing.every")} ${frequency} ${t(`checkout.billing.${interval}s`)}`;
