 1→"use server";
 2→
 3→import { getCustomerId } from "@/lib/paddle/get-customer-id";
 4→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 5→
 6→export async function createPaymentMethodChangeTransaction(): Promise<{
 7→  transactionId?: string;
 8→  error?: string;
 9→}> {
10→  try {
11→    const customerId = await getCustomerId();
12→    if (!customerId) return { error: "Missing customer" };
13→    const paddle = getPaddleInstance();
14→    // Find an active subscription for this customer
15→    const subs = paddle.subscriptions.list({ customerId: [customerId], perPage: 5 });
16→    const list = await subs.next();
17→    const active = (list || []).find((s: any) => (s.status || "").toLowerCase() === "active");
18→    const target = active || (list || [])[0];
19→    if (!target) return { error: "No subscription found" };
20→    const tx = await paddle.subscriptions.getPaymentMethodChangeTransaction(target.id);
21→    return { transactionId: tx.id };
22→  } catch (e: any) {
23→    console.error("Error creating payment method change transaction:", e);
24→    return { error: e?.message || "Failed to create payment method change transaction" };
25→  }
26→}
