 1→"use server";
 2→
 3→import { getCustomerId } from "@/lib/paddle/get-customer-id";
 4→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 5→import { parseSDKResponse } from "@/lib/paddle/data-helpers";
 6→
 7→export interface PaymentMethodSummary {
 8→  id: string;
 9→  brand?: string;
10→  last4?: string;
11→  expiryMonth?: number;
12→  expiryYear?: number;
13→  savedAt?: string;
14→}
15→
16→export async function getPrimaryPaymentMethod(
17→  overrideCustomerId?: string
18→): Promise<{
19→  method: PaymentMethodSummary | null;
20→  error?: string;
21→}> {
22→  try {
23→    async function fetchByCustomer(cid: string) {
24→      const paddle = getPaddleInstance();
25→      const collection = paddle.paymentMethods.list(cid, {
26→        perPage: 50,
27→        orderBy: "updated_at_desc",
28→      } as any);
29→      const list = await collection.next();
30→      return parseSDKResponse(list || []);
31→    }
32→
33→    const primaryId = overrideCustomerId || (await getCustomerId());
34→    if (!primaryId) return { method: null };
35→
36→    let methods: any[] = await fetchByCustomer(primaryId);
37→    // If override id returns nothing, try fallback id
38→    if ((!methods || methods.length === 0) && overrideCustomerId) {
39→      const fallbackId = await getCustomerId();
40→      if (fallbackId && fallbackId !== overrideCustomerId) {
41→        methods = await fetchByCustomer(fallbackId);
42→      }
43→    }
44→
45→    if (!methods || methods.length === 0) return { method: null };
46→
47→    // Prefer card methods first, then most recently updated; if none, allow PayPal
48→    const cardSorted = methods
49→      .filter((m: any) => !!m.card)
50→      .sort((a: any, b: any) =>
51→        (b.updatedAt || b.savedAt || "").localeCompare(
52→          a.updatedAt || a.savedAt || ""
53→        )
54→      );
55→    const m = (cardSorted[0] || methods[0]) as any;
56→    const summary: PaymentMethodSummary = {
57→      id: m.id,
58→      brand: m.card?.type || (m.paypal ? "paypal" : undefined),
59→      last4: m.card?.last4,
60→      expiryMonth: m.card?.expiryMonth,
61→      expiryYear: m.card?.expiryYear,
62→      savedAt: m.savedAt,
63→    };
64→    return { method: summary };
65→  } catch (e: any) {
66→    console.error("Error fetching payment methods:", e);
67→    return { method: null, error: e?.message || "Failed to fetch payment methods" };
68→  }
69→}
70→

