 5→import { getErrorMessage, parseSDKResponse } from "@/lib/paddle/data-helpers";
 6→import { getPaddleInstance } from "@/lib/paddle/get-paddle-instance";
 7→
 8→export async function getSubscriptions(): Promise<SubscriptionResponse> {
 9→  try {
10→    const customerId = await getCustomerId();
11→    if (customerId) {
12→      const subscriptionCollection = getPaddleInstance().subscriptions.list({
13→        customerId: [customerId],
14→        perPage: 20,
15→        include: ['next_transaction', 'recurring_transaction_details'],
16→      });
17→      const subscriptions = await subscriptionCollection.next();
18→      return {
19→        data: parseSDKResponse(subscriptions ?? []),
20→        hasMore: subscriptionCollection.hasMore,
21→        totalRecords: subscriptionCollection.estimatedTotal,
22→      };
23→    }
24→    // No customer yet (user never paid) — return empty without error
25→    return { data: [], hasMore: false, totalRecords: 0 };
26→  } catch (e) {
27→    console.error("Error fetching subscriptions:", e);
28→    return getErrorMessage();
29→  }
30→  // Unreachable, but satisfy return type
31→  // (we already return above when no customer)
32→}
