 29→
 30→  const handleCheckoutEvents = (event: CheckoutEventsData) => {
 31→    setCheckoutData(event);
 32→  };
 33→
 34→  const updateItems = useCallback(
 35→    throttle((paddle: Paddle, priceId: string, quantity: number) => {
 36→      paddle.Checkout.updateItems([{ priceId, quantity }]);
 37→    }, 1000),
 38→    []
 39→  );
 40→
 41→  useEffect(() => {
 42→    if (
 43→      !paddle?.Initialized &&
 44→      process.env.NEXT_PUBLIC_PADDLE_CLIENT_TOKEN &&
 45→      process.env.NEXT_PUBLIC_PADDLE_ENV
 46→    ) {
 47→      initializePaddle({
