115→/**
116→ * Get current month's usage for the user
117→ */
118→export async function getCurrentMonthUsage(): Promise<{
119→  usage: MonthlyUsage | null;
120→  error?: string;
121→}> {
122→  try {
123→    const supabase = await createServerSupabase();
124→
125→    // Get current user
126→    const {
127→      data: { user },
128→      error: authError,
129→    } = await supabase.auth.getUser();
130→
131→    if (authError || !user) {
132→      return {
133→        usage: null,
134→        error: "User not authenticated",
135→      };
136→    }
137→
138→    const currentMonth = new Date().toISOString().slice(0, 7); // YYYY-MM format
139→
140→    // Get or create monthly usage record
141→    let { data: usage, error } = await supabase
142→      .from("monthly_usage")
143→      .select("*")
144→      .eq("user_id", user.id)
145→      .eq("year_month", currentMonth)
146→      .single();
147→
148→    if (error && error.code === "PGRST116") {
149→      // Record doesn't exist, create it
150→      const { data: newUsage, error: createError } = await supabase
151→        .from("monthly_usage")
152→        .insert({
153→          user_id: user.id,
154→          year_month: currentMonth,
155→          documents_created: 0,
156→          published_completed_count: 0,
157→        })
158→        .select()
159→        .single();
160→
161→      if (createError) {
162→        console.error("Create usage record error:", createError);
163→        return {
164→          usage: null,
165→          error: "Failed to create usage record",
166→        };
167→      }
168→
169→      usage = newUsage;
170→    } else if (error) {
171→      console.error("Get usage error:", error);
172→      return {
173→        usage: null,
174→        error: "Failed to get usage",
175→      };
176→    }
177→
178→    return {
179→      usage: usage as MonthlyUsage,
180→    };
181→  } catch (error) {
