190→/**
191→ * Get user's usage limits and current status
192→ */
193→export async function getUserUsageLimits(): Promise<{
194→  limits: UsageLimits | null;
195→  error?: string;
196→}> {
197→  try {
198→    const [subscriptionResult, usageResult] = await Promise.all([
199→      getCurrentSubscription(),
200→      getCurrentMonthUsage(),
201→    ]);
202→
203→    if (usageResult.error || !usageResult.usage) {
204→      return {
205→        limits: null,
206→        error: usageResult.error || "Failed to get usage",
207→      };
208→    }
209→
210→    const { subscription } = subscriptionResult;
211→    const { usage } = usageResult;
212→
213→    const supabase = await createServerSupabase();
214→
215→    // If no active subscription, fall back to the lowest (by order) active plan (Free)
216→    let monthlyLimit: number;
217→    let activeLimit: number;
218→    if (!subscription) {
219→      const { data: fallbackPlans } = await supabase
220→        .from("subscription_plans")
221→        .select("monthly_document_limit, active_document_limit")
222→        .eq("is_active", true)
223→        .order("order", { ascending: true })
224→        .limit(1);
225→      monthlyLimit = fallbackPlans?.[0]?.monthly_document_limit ?? 0;
226→      activeLimit = fallbackPlans?.[0]?.active_document_limit ?? 0;
227→    } else {
228→      monthlyLimit = subscription.plan.monthly_document_limit;
229→      activeLimit = subscription.plan.active_document_limit;
230→    }
231→
232→    const limits: UsageLimits = {
233→      monthlyCreationLimit: monthlyLimit,
234→      activeDocumentLimit: activeLimit,
235→      canCreateNew:
236→        monthlyLimit === -1 || usage.documents_created < monthlyLimit,
237→      canPublishMore:
238→        activeLimit === -1 || usage.published_completed_count < activeLimit,
239→      currentMonthlyCreated: usage.documents_created,
240→      currentActiveDocuments: usage.published_completed_count,
241→    };
242→
243→    return {
244→      limits,
245→    };
246→  } catch (error) {
247→    console.error("Get usage limits error:", error);
248→    return {
249→      limits: null,
250→      error: "An unexpected error occurred",
251→    };
252→  }
253→}
