255→/**
256→ * Check if user can create a new document
257→ */
258→export async function canCreateDocument(): Promise<{
259→  canCreate: boolean;
260→  usingCredit?: boolean;
261→  reason?: string;
262→  error?: string;
263→}> {
264→  try {
265→    const { limits, error } = await getUserUsageLimits();
266→
267→    if (error || !limits) {
268→      return {
269→        canCreate: false,
270→        error: error || "Failed to check limits",
271→      };
272→    }
273→
274→    // Check monthly limit first
275→    if (limits.canCreateNew) {
276→      return { canCreate: true, usingCredit: false };
277→    }
278→
279→    // If monthly limit reached, check credits
280→    const { credits, error: creditsError } = await getCreditBalance();
281→    if (creditsError) {
282→      return { canCreate: false, error: creditsError };
283→    }
284→
285→    if (credits && credits.create_credits > 0) {
286→      return { canCreate: true, usingCredit: true };
287→    }
288→
289→    return {
290→      canCreate: false,
291→      reason: "월 문서 생성 한도 및 크레딧 모두 소진",
292→    };
293→  } catch (error) {
294→    console.error("Can create document error:", error);
295→    return {
296→      canCreate: false,
297→      error: "An unexpected error occurred",
298→    };
299→  }
300→}
