552→        error: "Failed to get usage",
553→      };
554→    }
555→
556→    // Update existing record
557→    const { error: updateError } = await supabase
558→      .from("monthly_usage")
559→      .update({
560→        published_completed_count: (usage?.published_completed_count || 0) + count,
561→      })
562→      .eq("user_id", user.id)
563→      .eq("year_month", currentMonth);
564→
565→    if (updateError) {
566→      console.error("Increment published completed error:", updateError);
567→      return {
568→        success: false,
569→        error: "Failed to update usage",
570→      };
571→    }
572→
573→    return {
574→      success: true,
575→    };
576→  } catch (error) {
577→    console.error("Increment published completed error:", error);
578→    return {
579→      success: false,
580→      error: "An unexpected error occurred",
581→    };
582→  }
583→}
