585→/**
586→ * Decrement monthly published/completed count when deleting a published/completed document
587→ */
588→export async function decrementPublishedCompleted(count: number = 1): Promise<{
589→  success: boolean;
590→  error?: string;
591→}> {
592→  try {
593→    const supabase = await createServerSupabase();
594→
595→    // Get current user
596→    const {
597→      data: { user },
598→      error: authError,
599→    } = await supabase.auth.getUser();
600→
601→    if (authError || !user) {
602→      return {
603→        success: false,
604→        error: "User not authenticated",
605→      };
606→    }
607→
608→    const currentMonth = new Date().toISOString().slice(0, 7); // YYYY-MM format
609→
610→    // Get or create usage record first
611→    let { data: usage, error: getError } = await supabase
612→      .from("monthly_usage")
613→      .select("*")
614→      .eq("user_id", user.id)
615→      .eq("year_month", currentMonth)
616→      .single();
617→
618→    if (getError && getError.code === "PGRST116") {
619→      // Record doesn't exist, create it with 0 count (nothing to decrement)
620→      return { success: true };
621→    } else if (getError) {
622→      console.error("Get usage error:", getError);
623→      return {
624→        success: false,
625→        error: "Failed to get usage",
626→      };
627→    }
628→
629→    // Only decrement if count is greater than 0
630→    if (usage && usage.published_completed_count > 0) {
631→      const newCount = Math.max(0, usage.published_completed_count - count);
632→      const { error: updateError } = await supabase
633→        .from("monthly_usage")
634→        .update({
635→          published_completed_count: newCount,
636→        })
637→        .eq("user_id", user.id)
638→        .eq("year_month", currentMonth);
639→
640→      if (updateError) {
641→        console.error("Decrement published completed error:", updateError);
642→        return {
643→          success: false,
644→          error: "Failed to update usage",
645→        };
646→      }
647→    }
648→
649→    return {
650→      success: true,
651→    };
652→  } catch (error) {
653→    console.error("Decrement published completed error:", error);
654→    return {
655→      success: false,
656→      error: "An unexpected error occurred",
657→    };
658→  }
659→}
