 97→/**
 98→ * Refund credit when a document is deleted
 99→ */
100→export async function refundCredit(
101→  type: "create" | "publish",
102→  documentId: string
103→): Promise<{ success?: boolean; error?: string }> {
104→  try {
105→    const supabase = await createServerSupabase();
106→
107→    const { data: { user }, error: authError } = await supabase.auth.getUser();
108→    if (authError || !user) {
109→      return { error: "User not authenticated" };
110→    }
111→
112→    const serviceSupabase = createServiceRoleClient();
113→
114→    // 1. Record refund transaction
115→    const { error: transactionError } = await serviceSupabase
116→      .from("credit_transactions")
117→      .insert({
118→        user_id: user.id,
119→        transaction_type: `refund_${type}`,
120→        create_credits: type === "create" ? 1 : 0,
121→        publish_credits: type === "publish" ? 1 : 0,
122→        related_document_id: documentId,
123→      });
124→
125→    if (transactionError) {
126→      console.error("Refund transaction error:", transactionError);
127→      return { error: "Failed to record refund transaction" };
128→    }
129→
130→    // 2. Increment credit balance
131→    const { data: balance } = await serviceSupabase
132→      .from("credit_balance")
133→      .select("create_credits, publish_credits")
134→      .eq("user_id", user.id)
135→      .single();
136→
137→    const { error: balanceError } = await serviceSupabase
138→      .from("credit_balance")
139→      .upsert({
140→        user_id: user.id,
141→        create_credits: (balance?.create_credits || 0) + (type === "create" ? 1 : 0),
142→        publish_credits: (balance?.publish_credits || 0) + (type === "publish" ? 1 : 0),
143→        updated_at: new Date().toISOString(),
144→      }, { onConflict: "user_id" });
145→
146→    if (balanceError) {
147→      console.error("Refund balance error:", balanceError);
148→      return { error: "Failed to refund credit balance" };
149→    }
150→
151→    return { success: true };
152→  } catch (error) {
153→    console.error("Refund credit error:", error);
154→    return { error: "An unexpected error occurred" };
155→  }
156→}
