158→/**
159→ * Deduct credit atomically using DB function
160→ */
161→export async function deductCredit(
162→  type: "create" | "publish",
163→  documentId: string
164→): Promise<{ success?: boolean; error?: string }> {
165→  try {
166→    const supabase = await createServerSupabase();
167→
168→    const { data: { user }, error: authError } = await supabase.auth.getUser();
169→    if (authError || !user) {
170→      return { error: "User not authenticated" };
171→    }
172→
173→    const serviceSupabase = createServiceRoleClient();
174→
175→    const { data, error } = await serviceSupabase.rpc("deduct_credit_atomic", {
176→      p_user_id: user.id,
177→      p_type: type,
178→      p_document_id: documentId,
179→    });
180→
181→    // Check for RPC error first
182→    if (error) {
183→      console.error("Deduct credit RPC error:", error);
184→      if (error.message?.includes("Insufficient credits")) {
185→        return { error: "크레딧이 부족합니다" };
186→      }
187→      return { error: "Failed to deduct credit" };
188→    }
189→
190→    // Check the boolean return value from deduct_credit_atomic
191→    if (!data) {
192→      console.error("Deduct credit failed: insufficient balance", {
193→        userId: user.id,
194→        type,
195→        documentId,
196→        returnValue: data,
197→      });
198→      return { error: "크레딧이 부족합니다" };
199→    }
200→
201→    // Only return success when data === true
202→    console.log("Credit deduction successful", {
203→      userId: user.id,
204→      type,
205→      documentId,
206→    });
207→    return { success: true };
208→  } catch (error) {
209→    console.error("Deduct credit error:", error);
210→    return { error: "An unexpected error occurred" };
211→  }
212→}
