214→/**
215→ * Add credits to user balance (called from webhook)
216→ * Uses atomic DB function to ensure:
217→ * - Idempotency (duplicate webhook protection via paddle_transaction_id UNIQUE constraint)
218→ * - Thread-safety (atomic transaction + balance update)
219→ * - Data consistency (transaction record + balance always in sync)
220→ */
221→export async function addCredits(
222→  userId: string,
223→  quantity: number,
224→  paddleTransactionId: string
225→): Promise<{ success?: boolean; error?: string }> {
226→  try {
227→    const serviceSupabase = createServiceRoleClient();
228→
229→    // Call atomic DB function
230→    const { data, error } = await serviceSupabase.rpc("add_credits_atomic", {
231→      p_user_id: userId,
232→      p_quantity: quantity,
233→      p_paddle_transaction_id: paddleTransactionId,
234→    });
235→
236→    if (error) {
237→      console.error("Add credits error:", error);
238→      return { error: "Failed to add credits" };
239→    }
240→
241→    // data is array of {success: boolean, error_message: text | null}
242→    const result = data?.[0];
243→
244→    if (!result?.success) {
245→      const errorMsg = result?.error_message || "Failed to add credits";
246→      console.error("Add credits failed:", errorMsg);
247→      return { error: errorMsg };
248→    }
249→
250→    // Log if this was a duplicate (idempotent retry)
251→    if (result.error_message === "Already processed") {
252→      console.log(`Idempotent webhook: ${paddleTransactionId} already processed`);
253→    }
254→
255→    return { success: true };
256→  } catch (error) {
257→    console.error("Add credits error:", error);
258→    return { error: "An unexpected error occurred" };
259→  }
260→}
