111→  async function onConfirmCancel() {
112→    if (!subscription) return;
113→    try {
114→      setCanceling(true);
115→      const subscriptionIdToCancel = subscription.id;
116→      const res = await cancelSubscription(
117→        subscriptionIdToCancel,
118→        "next_billing_period"
119→      );
120→      if (!res.ok) {
121→        toast({ title: t("bills.cancel.failed"), description: res.error });
122→        return;
123→      }
124→      toast({ title: t("bills.cancel.scheduled") });
125→      // Refresh subscription details
126→      const fresh = await getSubscription(subscriptionId);
127→      if (fresh.data) setSubscription(fresh.data);
128→      // Notify parent to refresh all data
129→      if (onCancelSuccess) await onCancelSuccess();
130→    } catch (err: any) {
131→      toast({ title: t("bills.cancel.failed"), description: err?.message });
132→    } finally {
133→      setCanceling(false);
134→    }
135→  }
136→
137→  return (
138→    <div className="space-y-6">
139→      {/* Subscription Header */}
140→      <div className="space-y-4">
141→        <div className="flex items-start justify-between gap-4">
142→          <div className="min-w-0">
143→            <h2 className="text-3xl font-medium truncate">
144→              {subscriptionItem.product.name}
145→            </h2>
146→            <div className="mt-3 flex items-center gap-6 flex-wrap">
147→              <div className="flex gap-1 items-end">
148→                <span className="text-3xl font-medium">{formattedPrice}</span>
149→                <span className="text-muted-foreground text-sm">
150→                  {frequency}
151→                </span>
152→              </div>
153→              <Status status={subscription.status} />
154→            </div>
155→          </div>
156→          {showCancel && (
157→            <AlertDialog>
158→              <AlertDialogTrigger asChild>
159→                <Button variant="outline" disabled={canceling}>
160→                  {canceling
161→                    ? t("bills.cancel.canceling")
162→                    : t("bills.cancel.action")}
163→                </Button>
164→              </AlertDialogTrigger>
165→              <AlertDialogContent>
166→                <AlertDialogHeader>
167→                  <AlertDialogTitle>{t("bills.cancel.title")}</AlertDialogTitle>
168→                  <AlertDialogDescription>
169→                    {t("bills.cancel.description")}
170→                  </AlertDialogDescription>
171→                </AlertDialogHeader>
172→                <AlertDialogFooter>
173→                  <AlertDialogCancel>
174→                    {t("bills.cancel.keep")}
175→                  </AlertDialogCancel>
176→                  <AlertDialogAction onClick={onConfirmCancel}>
177→                    {t("bills.cancel.confirm")}
178→                  </AlertDialogAction>
179→                </AlertDialogFooter>
180→              </AlertDialogContent>
181→            </AlertDialog>
182→          )}
183→        </div>
184→        <div className="text-muted-foreground text-base">
185→          {t("bills.startedOn")} {formattedStartedDate}
186→        </div>
187→      </div>
188→
189→    </div>
190→  );
191→}
