 13→import { PreviousPayments } from "@/components/bills/previous-payments";
 14→import { getTransactions } from "@/lib/paddle/get-transactions";
 15→
 16→export function SubscriptionsTab() {
 17→  const { t } = useLanguage();
 18→  const [subscriptions, setSubscriptions] = useState<Subscription[]>([]);
 19→  const [transactions, setTransactions] = useState<Transaction[]>([]);
 20→  const [loading, setLoading] = useState(true);
 21→  const [error, setError] = useState<string | null>(null);
 22→  const [txError, setTxError] = useState<string | null>(null);
 23→
 24→  async function fetchSubscriptions() {
 25→    try {
 26→      const { data, error: fetchError } = await getSubscriptions();
 27→
 28→      if (fetchError) {
 29→        setError(fetchError);
 30→      } else {
 31→        setSubscriptions(data || []);
 32→      }
 33→    } catch (err) {
 34→      console.error("Error:", err);
 35→      setError(t("bills.error.loadSubscriptions"));
 36→    } finally {
 37→      setLoading(false);
 38→    }
 39→  }
 40→
 41→  async function fetchTransactions() {
 42→    try {
 43→      const { data } = await getTransactions("", "");
 44→      setTransactions(data || []);
 45→    } catch (err) {
 46→      console.error("Error fetching transactions:", err);
 47→    }
 48→  }
 49→
 50→  async function refetchData() {
 51→    await Promise.all([fetchSubscriptions(), fetchTransactions()]);
 52→  }
 53→
 54→  useEffect(() => {
 55→    fetchSubscriptions();
 56→    fetchTransactions();
 57→  }, []);
 58→
 59→  // Previous payments load within PreviousPayments component
 60→
 61→  if (loading) {
 62→    return <LoadingScreen />;
 63→  }
 64→
 65→  if (error) {
 66→    return (
 67→      <Alert variant="destructive">
 68→        <AlertCircle className="h-4 w-4" />
 69→        <AlertDescription>{error}</AlertDescription>
 70→      </Alert>
 71→    );
 72→  }
 73→
 74→  // Filter only active subscriptions (active or trialing)
 75→  // Canceled/past_due subscriptions should not be shown
 76→  const activeSubscriptions = subscriptions.filter(
 77→    (sub) => sub.status === "active" || sub.status === "trialing"
 78→  );
 79→
 80→  if (activeSubscriptions.length === 0) {
 81→    return <NoSubscriptionView />;
 82→  }
 83→
 84→  // For now, show only the first active subscription
 85→  const isCanceling = activeSubscriptions[0]?.scheduledChange?.action === "cancel";
 86→
 87→  return (
 88→    <div className="space-y-6">
 89→      <SubscriptionDetail subscriptionId={activeSubscriptions[0].id} onCancelSuccess={refetchData} />
 90→      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 auto-rows-fr items-stretch">
 91→        <div className="h-full">
 92→          <PaymentMethodCard transactions={transactions} subscription={activeSubscriptions[0]} />
 93→        </div>
 94→        <div className="h-full">
 95→          {txError ? (
 96→            <Alert variant="destructive">
 97→              <AlertCircle className="h-4 w-4" />
 98→              <AlertDescription>{txError}</AlertDescription>
 99→            </Alert>
100→          ) : (
101→            <PreviousPayments transactions={transactions} />
102→          )}
103→        </div>
104→      </div>
105→    </div>
106→  );
107→}
