 1→import { createServerSupabase } from "@/lib/supabase/server";
 2→
 3→export async function getCustomerId() {
 4→  const supabase = await createServerSupabase();
 5→  const {
 6→    data: { user },
 7→  } = await supabase.auth.getUser();
 8→
 9→  if (user?.email) {
10→    const { data: customersData } = await supabase
11→      .from("customers")
12→      .select("customer_id,email")
13→      .eq("email", user.email)
14→      .single();
15→
16→    if (customersData?.customer_id) {
17→      return customersData.customer_id as string;
18→    }
19→  }
20→  return "";
21→}
