 47→  function extractFeaturesByLanguage(raw: unknown): string[] {
 48→    try {
 49→      if (!raw) return [];
 50→      if (Array.isArray(raw) && raw.every((x) => typeof x === "string")) {
 51→        return raw as string[];
 52→      }
 53→      if (typeof raw === "string") {
 54→        const parsed = JSON.parse(raw);
 55→        if (
 56→          parsed &&
 57→          typeof parsed === "object" &&
 58→          Array.isArray(parsed[language])
 59→        ) {
 60→          return parsed[language] as string[];
 61→        }
 62→      }
 63→      if (
 64→        typeof raw === "object" &&
 65→        raw !== null &&
 66→        Array.isArray((raw as any)[language])
 67→      ) {
 68→        return (raw as Record<string, string[]>)[language] || [];
