418→                                  count: plan.monthly_document_limit,
419→                                })}
420→                          </span>
421→                        </div>
422→                        {/* Removed Active Documents line as requested */}
423→                        {extractFeaturesByLanguage((plan as any).features).map((feature, index) => (
424→                          <div key={index} className="flex items-center gap-3">
425→                            <Check className="h-4 w-4 text-green-500 flex-shrink-0" />
426→                            <span className="text-sm">{feature}</span>
427→                          </div>
428→                        ))}
429→                      </div>
430→
431→                      {/* CTA Button */}
432→                      <Button
433→                        className={`w-full mt-auto ${
434→                          isLower ? "opacity-50 cursor-not-allowed" : ""
435→                        }`}
436→                        variant={isPopular ? "default" : "outline"}
437→                        disabled={isCurrent || isLower}
