529→
530→          {/* Additional Info */}
531→          <div className="mt-12 text-center text-sm text-muted-foreground">
532→            <p>{t("pricingPage.additionalInfo")}</p>
533→            <p className="mt-2">{t("pricingPage.additionalInfo2")}</p>
534→          </div>
