 1→import { createServerClient } from "@supabase/ssr";
 2→import { createClient } from "@supabase/supabase-js";
 3→import { cookies } from "next/headers";
 4→
 5→export async function createServerSupabase() {
 6→  const cookieStore = cookies();
 7→
 8→  return createServerClient(
 9→    process.env.NEXT_PUBLIC_SUPABASE_URL!,
10→    process.env.NEXT_PUBLIC_SUPABASE_PUBLISHABLE_KEY!,
11→    {
12→      cookies: {
13→        getAll() {
14→          return cookieStore.getAll();
15→        },
16→        setAll(cookiesToSet) {
17→          try {
18→            cookiesToSet.forEach(({ name, value, options }) =>
19→              cookieStore.set(name, value, options)
20→            );
21→          } catch {
22→            // The `setAll` method was called from a Server Component.
23→            // This can be ignored if you have middleware refreshing
24→            // user sessions.
25→          }
26→        },
27→      },
28→    }
29→  );
30→}
31→
32→// Service role client for admin operations (Storage, etc.)
33→export function createServiceSupabase() {
34→  return createClient(
35→    process.env.NEXT_PUBLIC_SUPABASE_URL!,
36→    process.env.SUPABASE_SERVICE_ROLE_KEY!,
37→    {
38→      auth: {
39→        autoRefreshToken: false,
40→        persistSession: false
41→      }
42→    }
43→  );
44→}
